import React, { useContext, useState } from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { UserContext } from '../Context';

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="https://mui.com/">
        Your Website
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

export default function LogIn() {
  const { login } = useContext(UserContext);
  const [userData, setUserData] = useState({
    username: "",
    password: ""
  });
  const nav = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/users/login', userData);
      if (response.status === 200) {
        login(response.data);
        nav("/home");
      }
    } catch (err) {
      console.error(err);
      nav('/singup');
    }
  };

  return (
    <Box
      sx={{
        display: 'flex',
        minHeight: '100vh',
        backgroundImage: `url(${process.env.PUBLIC_URL + "/סדנאות_בישול_לעובדים_בחברת_הייטק.jpg"})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        justifyContent: 'center',
        alignItems: 'center',
        padding: 2,
      }}
    >
      <Container component="main" maxWidth="xs" sx={{ zIndex: 1 }}>
        <CssBaseline />
        <Box
          sx={{
            marginBottom: 3,  // הזזנו את הטופס למעלה טיפה
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            padding: 10,
            borderRadius: 2,
            boxShadow: '0 4px 8px rgba(0,0,0,0.2)', 
            backgroundColor: 'transparent', 
          }}
        >
          <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography component="h1" variant="h5" sx={{ color: 'black' }}>
            התחברות
          </Typography>
          <Box component="form" noValidate sx={{ mt: 1 }} onSubmit={handleSubmit}>
            <TextField
              margin="normal"
              required
              fullWidth
              id="name"
              label="שם משתמש"
              name="name"
              autoComplete="name"
              autoFocus
              onBlur={(e) => { setUserData({ ...userData, username: e.target.value }); }}
              sx={{
                '& .MuiInputLabel-root': { color: 'black' }, // צבע הטקסט בכפתור
                '& .MuiOutlinedInput-root': { color: 'black' }, // צבע הטקסט בשדה
              }}
            />
            <TextField
              margin="normal"
              required
              fullWidth
              name="password"
              label="סיסמא"
              type="password"
              id="password"
              autoComplete="current-password"
              onBlur={(e) => { setUserData({ ...userData, password: e.target.value }); }}
              sx={{
                '& .MuiInputLabel-root': { color: 'black' }, // צבע הטקסט בכפתור
                '& .MuiOutlinedInput-root': { color: 'black' }, // צבע הטקסט בשדה
              }}
            />
            <FormControlLabel
              control={<Checkbox value="remember" color="primary" />}
              label="זכור אותי"
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{
                mt: 3, mb: 2,
                backgroundColor: 'black',  // צבע רקע לשחור
                color: 'white', // צבע טקסט בלבן
                '&:hover': { backgroundColor: 'black' } // צבע רקע בשעת ריחוף
              }}
            >
              התחברות
            </Button>
            <Grid container>
              <Grid item xs>
                <Link href="#" variant="body2" sx={{ color: 'black' }}>
                  ?שכחת את הסיסמא
                </Link>
              </Grid>
              <Grid item>
                <Link href="/singup" variant="body2" sx={{ color: 'black' }}>
                  {"אין לך חשבון? לחץ להרשמה"}
                </Link>
              </Grid>
            </Grid>
          </Box>
        </Box>
      </Container>
      <Box mt={8}>
        <Copyright />
      </Box>
    </Box>
  );
}
