import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { 
  Container, Typography, Box, CardMedia, 
  Table, TableBody, TableCell, TableContainer, 
  TableHead, TableRow, Paper, List, ListItem, 
  ListItemText 
} from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';

// üé® ◊ô◊¶◊ô◊®◊™ Theme ◊û◊ï◊™◊ê◊ù ◊ê◊ô◊©◊ô◊™ ◊¢◊ù ◊§◊ï◊†◊ò◊ô◊ù ◊ô◊§◊ô◊ù
const theme = createTheme({
  typography: {
    fontFamily: '"Assistant", sans-serif',
    h3: {
      fontFamily: '"Amatic SC", cursive',
      fontSize: '3rem',
      fontWeight: 700,
    },
    h4: {
      fontFamily: '"Amatic SC", cursive',
      fontSize: '2rem',
      fontWeight: 700,
    },
    body1: {
      fontSize: '1.2rem',
      fontWeight: 400,
    },
  },
});

const OneCategory = () => {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:3000/recipes/${id}`)
      .then(response => {
        setRecipe(response.data);
      })
      .catch(err => {
        console.error("Error fetching recipe:", err);
      });
  }, [id]);

  if (!recipe) {
    return <div>◊ò◊ï◊¢◊ü...</div>;
  }

  return (
    <ThemeProvider theme={theme}>
      <Container sx={{ mt: 4 }}>
        {/* ◊õ◊ï◊™◊®◊™ */}
        <Typography variant="h3" align="center" gutterBottom sx={{ mt: 2, fontWeight: 'bold', textDecoration: 'underline' }}>
          {recipe.name}
        </Typography>

        {/* ◊§◊®◊ò◊ô ◊û◊™◊õ◊ï◊ü */}
        <Box sx={{ display: 'flex', justifyContent: 'center', gap: 4, mt: 2 }}>
          <Typography variant="h6"><strong>‚è≥ ◊ñ◊û◊ü ◊î◊õ◊†◊î:</strong> {recipe.preparationTime} ◊ì◊ß◊ï◊™</Typography>
          <Typography variant="h6"><strong>üçΩ ◊õ◊û◊ï◊™:</strong> {recipe.finalYield}</Typography>
        </Box>

        {/* ◊™◊û◊ï◊†◊™ ◊î◊û◊™◊õ◊ï◊ü */}
        <CardMedia
          component="img"
          image={`http://localhost:3000${recipe.image}`}
          alt={recipe.name}
          sx={{
            width: "100%",
            maxWidth: 500,
            height: 300,
            borderRadius: 2,
            boxShadow: 3,
            objectFit: "cover",
            mx: "auto",
            display: "block"
          }}
        />

        {/* ◊ò◊ë◊ú◊™ ◊û◊¶◊®◊õ◊ô◊ù */}
        <Box sx={{ mt: 4 }}>
          <Typography variant="h5" gutterBottom sx={{ fontWeight: 'bold', textAlign: 'center', bgcolor: '#f5f5f5', py: 1, borderRadius: 1 }}>
            ◊û◊¶◊®◊õ◊ô◊ù
          </Typography>
          <TableContainer component={Paper} sx={{ maxWidth: 500, mx: 'auto' }}>
            <Table>
              <TableHead>
                <TableRow sx={{ bgcolor: '#ddd' }}>
                  <TableCell align="center"><strong>◊õ◊û◊ï◊™</strong></TableCell>
                  <TableCell align="center"><strong>◊û◊¶◊®◊ö</strong></TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {recipe.ingredients.map((ingredient, index) => {
                  const [amount, name] = ingredient.split(' ', 2);
                  return (
                    <TableRow key={index}>
                      <TableCell align="center">{amount || '-'}</TableCell>
                      <TableCell align="center">{name || ingredient}</TableCell>
                    </TableRow>
                  );
                })}
              </TableBody>
            </Table>
          </TableContainer>
        </Box>

        {/* ◊©◊ú◊ë◊ô ◊î◊õ◊†◊î */}
        <Box sx={{ mt: 4 }}>
          <Typography variant="h5" gutterBottom sx={{ fontWeight: 'bold', textAlign: 'center', bgcolor: '#f5f5f5', py: 1, borderRadius: 1 }}>
            ◊©◊ú◊ë◊ô ◊î◊õ◊†◊î
          </Typography>
          <Paper elevation={3} sx={{ p: 2, mt: 2 }}>
            <List>
              {recipe.preparationSteps.map((step, index) => (
                <ListItem key={index}>
                  <ListItemText primary={`üîπ ${step}`} />
                </ListItem>
              ))}
            </List>
          </Paper>
        </Box>
      </Container>
    </ThemeProvider>
  );
};

export default OneCategory;
