import React, { useState, useEffect } from "react";
import axios from 'axios';
import { TextField, Box, Typography, Card, CardContent } from '@mui/material';
import { useNavigate } from "react-router-dom";
import SearchIcon from '@mui/icons-material/Search';

const Search = () => {
    const [recipes, setRecipes] = useState([]);
    const [searchQuery, setSearchQuery] = useState(""); //  住 转 注专 砖 砖 驻砖
    const [filteredRecipes, setFilteredRecipes] = useState([]); // 住 转 转 驻专 驻 驻砖
    const nav = useNavigate();

    useEffect(() => {
        const getAllRecipes = async () => {
            try {
                const response = await axios.get('http://localhost:3000/recipes'); // 注 转转 API 转
                setRecipes(response.data); // 砖  砖注 转 `recipes` 注 转 砖转拽 -API
            } catch (err) {
                console.error(err);
            }
        };
        getAllRecipes();
    }, []); // 专爪 驻注 转 砖专 `Search` 注

    // 驻拽爪 砖驻转 驻砖 住转 转 转
    const handleSearchChange = (event) => {
        const query = event.target.value;
        setSearchQuery(query); // 注 -state 砖 驻砖
        if (query === "") {
            setFilteredRecipes([]); //  驻砖 专拽,  专 转  转
        } else {
            const filtered = recipes.filter(recipe =>
                recipe.name.toLowerCase().includes(query.toLowerCase()) // 驻砖 砖 转  砖转
            );
            setFilteredRecipes(filtered); // 注 转 驻专
        }
    };

    // 驻拽爪 注专  转 砖驻转 砖 驻砖
    const handleRecipeClick = (recipeId) => {
        nav("/recipe/" + recipeId); //  注 转
        setSearchQuery(''); // 拽 转 砖 驻砖 专 
        setFilteredRecipes([]); // 驻住 转 转 驻专
    };

    return (
        <Box sx={{ position: 'relative', top: '9px', direction: 'rtl', left: '63px' }}>
            {/* 砖 驻砖 */}

            <TextField
                label="驻砖 转...  "
                variant="outlined"
                value={searchQuery} // 砖专 注 注专 -input
                onChange={handleSearchChange} // 拽专 驻拽爪  驻注 砖砖转砖 拽
                fullWidth
                sx={{
                    marginBottom: 2,
                    borderRadius: '15px',  // 驻转 注转
                    backgroundColor: 'transparent', // 专拽注 砖拽祝
                    '& .MuiOutlinedInput-root': {
                        '& fieldset': {
                            borderColor: 'white', // 爪注  
                        },
                        '&:hover fieldset': {
                            borderColor: 'white', // 爪注  爪
                        },
                    },
                    '& .MuiInputLabel-root': {
                        color: 'white', // 爪注 转转 
                    },
                    '& .MuiInputBase-input': {
                        color: 'black', // 爪注 拽住 
                    },
                    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)', // 爪 拽拽
                }} // 住 -TextField

            />

            {/* 转爪 砖 驻砖 - 爪转 转爪 专拽  砖 驻砖 */}
            {searchQuery && (
                <Box sx={{
                    color: "black",
                    position: 'absolute',
                    backgroundColor: 'white',
                    width: '100%',
                    maxHeight: '400px',
                    overflowY: 'auto',
                    padding: '8px',
                    boxShadow: 2
                }}>
                    {filteredRecipes.length === 0 ? (
                        <Typography variant="body2" sx={{ fontSize: '10px' }}> 爪 转 转.</Typography>
                    ) : (
                        filteredRecipes.map((recipe) => (
                            <Card key={recipe._id} sx={{ marginBottom: 2 }} >
                                <CardContent onClick={() => handleRecipeClick(recipe._id)}>
                                    <Typography variant="body1" sx={{ fontSize: '12px', fontWeight: 600 }}>
                                        {recipe.name}
                                    </Typography>
                                    <Typography variant="body2" color="text.secondary" sx={{ fontSize: '10px' }}>
                                        {recipe.description}
                                    </Typography>
                                </CardContent>
                            </Card>
                        ))
                    )}
                </Box>
            )}
        </Box>
    );
};

export default Search;
