import React, { useContext, useState } from 'react';
import Avatar from '@mui/material/Avatar';
import { Box, Button, Container, Link, TextField, FormControlLabel, Checkbox, Grid, Typography, Alert } from '@mui/material';
import CssBaseline from '@mui/material/CssBaseline';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import axios from 'axios';
import { UserContext } from '../Context';
import { useNavigate } from 'react-router-dom';

export default function SignUp() {
  const { login } = useContext(UserContext);
  const [user, setUser] = useState({
    username: "",
    email: "",
    password: ""
  });

  const [type,setType]= useState("");;
  const [message, setMessage] = useState("");
  const [errors, setErrors] = useState({ username: "", email: "", password: "" });

  const validateUsername = (value) => {
    if (value.length < 3) return "שם משתמש חייב להכיל לפחות 3 תווים";
    if (value.length > 20) return "שם משתמש לא יכול להכיל יותר מ-20 תווים";
    return "";
  };

  const validateEmail = (value) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(value) ? "" : "כתובת מייל לא תקינה";
  };

  const validatePassword = (value) => {
    return value.length < 6 ? "הסיסמה חייבת להכיל לפחות 6 תווים" : "";
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser({ ...user, [name]: value });

    let errorMsg = "";
    if (name === "username") errorMsg = validateUsername(value);
    if (name === "email") errorMsg = validateEmail(value);
    if (name === "password") errorMsg = validatePassword(value);

    setErrors({ ...errors, [name]: errorMsg });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (errors.username || errors.email || errors.password || !user.username || !user.email || !user.password) {
      setType()
      setMessage("יש לתקן את כל השגיאות לפני שליחה");
      return;
    }

    try {
      const response = await axios.post('http://localhost:3000/users/signup', user);
      if (response.status === 200) {
        login(response.data);
        setMessage("משתמש נוסף בהצלחה");
      }
    } catch (error) {
      console.log("Error:", error);
    }
  };

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <Box sx={{ marginTop: 8, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <Avatar sx={{ margin: 1, backgroundColor: 'secondary.main' }}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">הרשמה</Typography>
        <Box component="form" noValidate sx={{ width: '100%', marginTop: 3 }} onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <TextField
                name="username"
                variant="outlined"
                required
                fullWidth
                label="שם משתמש"
                error={!!errors.username}
                helperText={errors.username}
                onChange={handleChange}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                name="email"
                variant="outlined"
                required
                fullWidth
                label="כתובת מייל"
                error={!!errors.email}
                helperText={errors.email}
                onChange={handleChange}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                name="password"
                variant="outlined"
                required
                fullWidth
                label="סיסמא"
                type="password"
                error={!!errors.password}
                helperText={errors.password}
                onChange={handleChange}
              />
            </Grid>
            <Grid item xs={12}>
              <FormControlLabel control={<Checkbox value="allowExtraEmails" color="primary" />} label="אני רוצה לקבל עדכונים במייל." />
            </Grid>
          </Grid>
          <Button type="submit" fullWidth variant="contained" color="primary" sx={{ marginTop: 3, marginBottom: 2 }}>הרשמה</Button>
          <Grid container justifyContent="flex-end">
            <Grid item>
              <Link href="/login" variant="body2">יש לך כבר חשבון? היכנס</Link>
            </Grid>
          </Grid>
        </Box>
      </Box>
      {message &&  <Alert severity="success">{message}</Alert>}
    </Container>
  );
}
