import axios from "axios";
import React, { useState, useEffect } from "react";

const meatRecipes = [
    // המתכונים שלך נשארים כפי שהם
    {
        image: "/uploads/steak.jpeg",
        publishDate: "2025-01-22",
        categoryCode: "679215da728744aafe74dfc4",
        preparationTime: 30,
        ingredients: ["בשר בקר", "שמן זית", "מלח", "פלפל שחור"],
        preparationSteps: ["לחמם מחבת עם שמן זית", "לתבל את הבשר", "לצרוב 3 דקות מכל צד"],
        finalYield: "2 מנות",
        likes: 25,
    },
    {
        image: "/uploads/kebab.jpeg",
        publishDate: "2025-01-23",
        categoryCode: "679215da728744aafe74dfc4",
        preparationTime: 40,
        ingredients: ["בשר טחון", "פטרוזיליה", "בצל", "תבלינים"],
        preparationSteps: ["לקצוץ בצל ופטרוזיליה", "לערבב עם הבשר והתבלינים", "לעצב לקבבים ולאפות"],
        finalYield: "6 מנות",
        likes: 40,
    },
    {
        image: "/uploads/shawarma.jpeg",
        publishDate: "2025-01-24",
        categoryCode: "679215da728744aafe74dfc4",
        preparationTime: 50,
        ingredients: ["שווארמה הודו", "תבלינים", "פיתה", "ירקות"],
        preparationSteps: ["לבשל את השווארמה עם תבלינים", "להגיש בפיתה עם ירקות"],
        finalYield: "4 מנות",
        likes: 55,
    },
    {
        image: "/uploads/burger.jpeg",
        publishDate: "2025-01-25",
        categoryCode: "679215da728744aafe74dfc4",
        preparationTime: 35,
        ingredients: ["בשר טחון", "לחמנייה", "עגבנייה", "חסה"],
        preparationSteps: ["להכין קציצת בשר", "לטגן ולהרכיב המבורגר בלחמנייה"],
        finalYield: "2 מנות",
        likes: 70,
    },
    {
        image: "/uploads/goulash.jpeg",
        publishDate: "2025-01-26",
        categoryCode: "679215da728744aafe74dfc4",
        preparationTime: 90,
        ingredients: ["בשר בקר", "בצל", "גזר", "פפריקה"],
        preparationSteps: ["לטגן בצל", "להוסיף בשר ותבלינים", "לבשל על אש קטנה"],
        finalYield: "6 מנות",
        likes: 60,
    },
    {
        image: "/uploads/kofta.jpeg",
        publishDate: "2025-01-27",
        categoryCode: "679215da728744aafe74dfc4",
        preparationTime: 45,
        ingredients: ["בשר טחון", "שום", "כמון", "פטרוזיליה"],
        preparationSteps: ["לערבב מרכיבים", "להכין קציצות ולצלות בתנור"],
        finalYield: "4 מנות",
        likes: 35,
    },
    {
        image: "/uploads/steak_and_potatoes.jpeg",
        publishDate: "2025-01-28",
        categoryCode: "679215da728744aafe74dfc4",
        preparationTime: 60,
        ingredients: ["סטייק אנטריקוט", "תפוחי אדמה", "שום", "רוזמרין"],
        preparationSteps: ["לתבל את הסטייק", "לאפות תפוחי אדמה בתנור"],
        finalYield: "3 מנות",
        likes: 80,
    },
    {
        image: "/uploads/meatballs.jpeg",
        publishDate: "2025-01-29",
        categoryCode: "679215da728744aafe74dfc4",
        preparationTime: 75,
        ingredients: ["בשר טחון", "רסק עגבניות", "בצל", "תבלינים"],
        preparationSteps: ["לערבב בשר ותבלינים", "לבשל ברוטב עגבניות"],
        finalYield: "5 מנות",
        likes: 50,
    },
];

const NewAddRecipe = () => {
    const [selectedImage, setSelectedImage] = useState(null);

    const handleImageChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            setSelectedImage(URL.createObjectURL(file)); // בחרת את התמונה והצגת אותה
        }
    };

    const addRecipes = async () => {
        for (const recipe of meatRecipes) {
            // עדכון שדה התמונה של כל מתכון בתמונה שנבחרה
            const updatedRecipe = {
                ...recipe,
                image: selectedImage || recipe.image, // אם לא נבחרה תמונה, נשאיר את התמונה המקורית
            };

            try {
                const response = await axios.post("http://localhost:3000/recipes", updatedRecipe, {
                    headers: {
                        "Content-Type": "application/json",
                    },
                });
                console.log(`Recipe added: ${updatedRecipe.image}`, response.data);
            } catch (error) {
                console.error(`Error adding recipe ${updatedRecipe.image}:`, error);
            }
        }
    };

    useEffect(() => {
        if (selectedImage) {
            addRecipes(); // אם נבחרה תמונה, התחיל את תהליך ההוספה
        }
    }, [selectedImage]);

    return (
        <div>
            <h2>Adding Recipes...</h2>
            <input type="file" onChange={handleImageChange} />
            {selectedImage && <img src={selectedImage} alt="Selected" width="100" />}
        </div>
    );
};

export default NewAddRecipe;
