import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Container, Typography, Box, Card, CardContent, CardMedia, Rating } from '@mui/material';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import CategoryIcon from '@mui/icons-material/Category';
import axios from 'axios';

const RecipeDetails = () => {
  const { id } = useParams();  // מקבל את ה-ID מה-URL
  const [recipe, setRecipe] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get(`http://localhost:3000/recipes/${id}`)
      .then(response => {
        setRecipe(response.data);
        setLoading(false);
      })
      .catch(err => {
        console.error(err);
        setLoading(false);
      });
  }, [id]);

  if (loading) {
    return <div>טוען...</div>;
  }

  if (!recipe) {
    return <div>המתכון לא נמצא</div>;
  }

  return (
    <Container>
      <Typography variant="h4" gutterBottom align="center">{recipe.name}</Typography>

      <Card sx={{ display: 'flex', flexDirection: 'column' }}>
        <CardMedia
          component="img"
          height="240"
          image={`http://localhost:3000${recipe.image}`}
          alt={recipe.name}
        />
        <CardContent sx={{ direction: "rtl" }}>
          <Typography variant="body1" gutterBottom>זמן הכנה: {recipe.preparationTime} דקות</Typography>
          <Typography variant="body1" gutterBottom>כמות: {recipe.finalYield}</Typography>

          <Typography variant="h6" gutterBottom>מצרכים:</Typography>
          <Box mb={2}>
            <ul>
              {recipe.ingredients.map((ingredient, index) => (
                <li key={index}>{ingredient}</li>
              ))}
            </ul>
          </Box>

          <Typography variant="h6" gutterBottom>שלבי הכנה:</Typography>
          <Box mb={2}>
            <ol>
              {recipe.preparationSteps.map((step, index) => (
                <li key={index}>{step}</li>
              ))}
            </ol>
          </Box>

          <Box display="flex" alignItems="center" sx={{
            padding: '5px 10px',
            borderRadius: '50px',
            alignItems: 'center',
            marginBottom: '10px',
            justifyContent: 'center',
          }}>
            <Rating
              name="read-only"
              value={recipe.likes / 10}
              readOnly
            />
          </Box>
        </CardContent>
      </Card>
    </Container>
  );
};

export default OneCategory;
