import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Container, Typography, Box, CardMedia, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, List, ListItem, ListItemText } from '@mui/material';

const OneCategory = () => {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:3000/recipes/${id}`)
      .then(response => {
        setRecipe(response.data);
      })
      .catch(err => {
        console.error("Error fetching recipe:", err);
      });
  }, [id]);

  if (!recipe) {
    return <div>注...</div>;
  }

  return (
    <Container sx={{ mt: 4 }}>

      <CardMedia
  component="img"
  image={`http://localhost:3000${recipe.image}`}
  alt={recipe.name}
  sx={{
    width: "100%", // 转  专 住
    maxWidth: 500, //   转专 
    height: 300, //  转
    borderRadius: 2,
    boxShadow: 3,
    objectFit: "cover", // 转注 注转 转转 驻
    mx: "auto", // 砖专 专
    display: "block" //  砖转  转转专 抓 拽专
  }}
/>




      <Box sx={{ mt: 4 }}>
        <Typography variant="h5" gutterBottom sx={{ fontWeight: 'bold', textAlign: 'center', bgcolor: '#f5f5f5', py: 1, borderRadius: 1 }}>
          爪专
        </Typography>
        <TableContainer component={Paper} sx={{ maxWidth: 500, mx: 'auto' }}>
          <Table>
            <TableHead>
              <TableRow sx={{ bgcolor: '#ddd' }}>
                <TableCell align="center"><strong>转</strong></TableCell>
                <TableCell align="center"><strong>爪专</strong></TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {recipe.ingredients.map((ingredient, index) => {
                const [amount, name] = ingredient.split(' ', 2); // 驻专 转 砖
                return (
                  <TableRow key={index}>
                    <TableCell align="center">{amount || '-'}</TableCell>
                    <TableCell align="center">{name || ingredient}</TableCell>
                  </TableRow>
                );
              })}
            </TableBody>
          </Table>
        </TableContainer>
      </Box>


      <Box sx={{ mt: 4 }}>
        <Typography variant="h5" gutterBottom sx={{ fontWeight: 'bold', textAlign: 'center', bgcolor: '#f5f5f5', py: 1, borderRadius: 1 }}>
          砖 
        </Typography>
        <Paper elevation={3} sx={{ p: 2, mt: 2 }}>
          <List>
            {recipe.preparationSteps.map((step, index) => (
              <ListItem key={index}>
                <ListItemText primary={` ${step}`} />
              </ListItem>
            ))}
          </List>
        </Paper>
      </Box>
    </Container>
  );
};

export default OneCategory;
