import axios from "axios";
import React, { useEffect, useState } from "react";

const Home = () => {
  const [categori, setCategori] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // קריאה לשרת כדי לקבל את כל המתכונים
  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await axios.get("http://localhost:3000/categories/getAllCategories",categori); // הנתיב ל-API שלך
        if (!response.ok) {
          throw new Error("Failed to fetch categori");
        }
        // const data = await response.json();
        // setCategori(data); // שמירת המתכונים ב-state
      } catch (err) {
        setError(err.message); // שמירת השגיאה
      } finally {
        setLoading(false); // סיום טעינה
      }
    };

    fetchRecipes();
  }, []);

  if (loading) {
    return <p>טוען נתונים...</p>;
  }

  if (error) {
    return <p>שגיאה: {error}</p>;
  }

  return (
    <div>
      <h1>מתכונים</h1>
      {categori.map((recipe) => (
        <div key={recipe._id}>
          <h2>{recipe.name}</h2>
          <p>קטגוריה: {recipe.categoryCode?.name || "ללא קטגוריה"}</p>
          {recipe.image && (
            <img
              src={`http://localhost:3000${recipe.image}`} // אם למתכון יש תמונה
              alt={recipe.name}
              width="200"
            />
          )}
        </div>
      ))}
    </div>
  );
};

export default Home;
