import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom"; 

const OneCategory = () => {
  const { id } = useParams(); // נקבל את ה-ID מתוך ה-URL
  const [recipe, setRecipe] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:3000/recipes/${id}`)
      .then(response => response.json())
      .then(data => setRecipe(data))
      .catch(error => console.error("Error:", error));
  }, [id]);

  if (!recipe) {
    return <div>טוען...</div>;
  }

  return (
    <div>
      <h1>{recipe.name}</h1>
      <img src={`http://localhost:3000${recipe.image}`} alt={recipe.name} />
      <p>זמן הכנה: {recipe.preparationTime} דקות</p>
      <p>כמות: {recipe.finalYield}</p>
      <p>{recipe.ingredients}</p>
      <p>{recipe.preparationSteps}</p>
      <p>דירוג: {recipe.likes}</p>
    </div>
  );
};

export default RecipeDetails;
