import React, { createContext, useState, useEffect } from "react";

export const UserContext = createContext();

export const UserProvider = ({ children }) => {
  const [user, setUser] = useState(null);

  // üöÄ ◊ò◊ï◊¢◊ü ◊ê◊™ ◊î◊û◊©◊™◊û◊© ◊û◊î-localStorage ◊õ◊©◊†◊ò◊¢◊ü ◊î◊ì◊£
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  const login = (newUser) => {
    if (newUser != null) {
      setUser(newUser);
      localStorage.setItem("user", JSON.stringify(newUser)); // ‚úÖ ◊©◊û◊ô◊®◊î
    }
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem("user"); // ‚ùå ◊û◊ó◊ô◊ß◊î
  };

  const updateUser = (user) => {
    if (user) {
      setUser(user);
      localStorage.setItem("user", JSON.stringify(user)); // ‚úÖ ◊¢◊ì◊õ◊ï◊ü ◊í◊ù ◊ë-localStorage
    }
  };

  return (
    <UserContext.Provider value={{ user, login, logout, updateUser }}>
      {children}
    </UserContext.Provider>
  );
};
