import axios from "axios";
import React, { useEffect } from "react";

const recipes = [
  {
    image: "/uploads/1737629783507.jpeg",
    publishDate: "2025-01-22",
    categoryCode: "679215da728744aafe74dfc4",
    preparationTime: 30,
    ingredients: ["קמח", "סוכר", "בצל"],
    preparationSteps: ["הכניסו את הקמח", "ערבבו את הכל"],
