    categoryCode: "679215da728744aafe74dfc4",
    preparationTime: 60,
    ingredients: ["עוף", "דבש", "חרדל"],
    preparationSteps: ["לערבב את הדבש והחרדל", "למרוח על העוף"],
    finalYield: 6,
    likes: 25,
  },
  {
