    categoryCode: "679215da728744aafe74dfc4",
    preparationTime: 40,
    ingredients: ["סטייק", "מלח", "פלפל"],
    preparationSteps: ["לצלות את הסטייק", "לתבל לפי הטעם"],
    finalYield: 2,
    likes: 30,
  },
  {
    image: "/uploads/1737629783525.jpeg",
    publishDate: "2025-01-16",
    categoryCode: "679215da728744aafe74dfc4",
    preparationTime: 90,
    ingredients: ["צלעות טלה", "שום", "יין אדום"],
    preparationSteps: ["למרוח שום על הצלעות", "לבשל ביין אדום"],
    finalYield: 5,
    likes: 20,
  },
  {
    image: "/uploads/1737629783530.jpeg",
    publishDate: "2025-01-15",
    categoryCode: "679215da728744aafe74dfc4",
    preparationTime: 50,
    ingredients: ["קבב", "בצל", "פטרוזיליה"],
    preparationSteps: ["לערבב את הבשר עם התבלינים", "לטגן במחבת"],
    finalYield: 8,
    likes: 18,
  },
  {
    image: "/uploads/1737629783535.jpeg",
    publishDate: "2025-01-14",
    categoryCode: "679215da728744aafe74dfc4",
    preparationTime: 35,
    ingredients: ["נקניקיות", "חרדל", "כרוב כבוש"],
    preparationSteps: ["לבשל את הנקניקיות", "להגיש עם חרדל וכרוב"],
    finalYield: 4,
    likes: 10,
  },
  {
    image: "/uploads/1737629783540.jpeg",
    publishDate: "2025-01-13",
    categoryCode: "679215da728744aafe74dfc4",
    preparationTime: 55,
    ingredients: ["בקר", "יין לבן", "גזר"],
    preparationSteps: ["לבשל את הבקר עם הירקות", "להוסיף יין ולבשל באיטיות"],
    finalYield: 6,
    likes: 22,
  },
];

const NewAddRecipe = () => {
  useEffect(() => {
    const addRecipes = async () => {
      for (const recipe of recipes) {
        try {
          const response = await axios.post("http://localhost:3000/recipes", recipe, {
            headers: {
              "Content-Type": "application/json",
            },
          });
          console.log(`Recipe added: ${recipe.image}`, response.data);
        } catch (error) {
          console.error(`Error adding recipe ${recipe.image}:`, error);
        }
      }
    };

    addRecipes();
  }, []);

  return (
    <div>
      <h2>Adding Recipes...</h2>
    </div>
  );
};

export default NewAddRecipe;
