import * as React from 'react';
import { AppProvider } from '@toolpad/core/AppProvider';
import { SignInPage } from '@toolpad/core/SignInPage';
import { useTheme } from '@mui/material/styles';
import { TextField } from '@mui/material';

// preview-start
const providers = [{ id: 'credentials', name: 'Email and Password' }];
// preview-end

const signIn = async (provider, formData) => {
  const promise = new Promise((resolve) => {
    setTimeout(() => {
      alert(
        `Signing in with "${provider.name}" and credentials: ${formData.get('email')}, ${formData.get('password')}, Name: ${formData.get('name')}`
      );
      resolve();
    }, 300);
  });
  return promise;
};

export default function CredentialsSignInPage() {
  const theme = useTheme();

  return (
    <AppProvider theme={theme}>
      <SignInPage
        signIn={signIn}
        providers={providers}
        slotProps={{
          emailField: { autoFocus: false },
          passwordField: { label: 'Password' }, // אם רוצים לשנות את הלייבל של הסיסמה
          nameField: { 
            component: TextField, // שדה שם מותאם אישית
            label: 'Full Name',
            name: 'name',
            variant: 'outlined',
            fullWidth: true,
            style: { marginBottom: '16px' }
          }
        }}
      />
    </AppProvider>
  );
}
