import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Container, Grid, Card, CardContent, CardMedia, Typography, Box } from '@mui/material';
import { Rating } from '@mui/material';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import CategoryIcon from '@mui/icons-material/Category';

const CategoryRecipe = () => {
  const { categoryId } = useParams(); // קבלת ה- categoryId מתוך ה-URL
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [categories, setCategories] = useState([]);


  useEffect(() => {
    // בקשה לשרת כדי לקבל את המתכונים לפי categoryId
    axios.get(`http://localhost:3000/recipes/category/${categoryId}`)
      .then(response => {
        console.log('response', response.data);

        setRecipes(response.data); // עדכון הנתונים של המתכונים
        setLoading(false); // סיום טעינה
      })
      .catch(err => {
        console.error(err); // טיפול בשגיאות
        setLoading(false); // סיום טעינה גם במקרה של שגיאה
      });
  }, [categoryId]); // התלוות ל- categoryId

useEffect(()=>{
    // בקשה לשרת כדי לקבל את כל הקטגוריות
    axios.get('http://localhost:3000/categories')
      .then(response => {
        setCategories(response.data);
      })
      .catch(err => console.error(err));

},[])


  if (loading) {
    return <div>טוען...</div>;
  }

  return (
    <Container>
      <Typography variant="h3" gutterBottom align="center">
        {response.data.n}
      </Typography>

      <Grid container spacing={4} justifyContent="center">
        {recipes.length === 0 ? (
          <Typography variant="h6" gutterBottom align="center">
            לא נמצאו מתכונים בקטגוריה הזו
          </Typography>
        ) : (
          recipes.map(recipe => (
            <Grid item xs={12} sm={6} md={4} key={recipe._id}>
              <Card
                sx={{
                  display: 'flex',
                  flexDirection: 'column',
                  '&:hover': {
                    transform: 'scale(1.05)',
                    boxShadow: 10,
                    transition: 'transform 0.3s ease-in-out',
                  },
                }}
              >
                <CardMedia
                  component="img"
                  height="140"
                  image={`http://localhost:3000${recipe.image}`}
                  alt={recipe.name}
                />
                <CardContent sx={{ direction: "rtl" }}>
                  {/* שם המתכון */}
                  <Typography variant="h6" gutterBottom sx={{ textAlign: 'center',textDecoration:'underline'}}>
                    {recipe.name}
                  </Typography>

                  <Box display="flex" alignItems="center" mb={3}>
                    {/* זמן הכנה */}
                    <AccessTimeIcon sx={{ mr: 1 }} />
                    <Typography variant="body2">{recipe.preparationTime} דקות</Typography>
                  </Box>

                  <Box display="flex" alignItems="center" mb={3}>
                    {/* כמות */}
                    <CategoryIcon sx={{ mr: 1 }} />
                    <Typography variant="body2">כמות: {recipe.finalYield}</Typography>
                  </Box>

                  <Box display="flex" alignItems="center" sx={{

                    padding: '5px 10px', 
                    borderRadius: '50px',
                    alignItems: 'center',
                    marginBottom: '10px',
                    justifyContent: 'center',
                  }}>
                    {/* דירוג כוכבים */}
                    <Rating 
                      name="read-only" 
                      value={recipe.likes/10} 
                      readOnly 
                    />
                  </Box>
                </CardContent>
              </Card>
            </Grid>
          ))
        )}
      </Grid>
    </Container>
  );
};

export default CategoryRecipe;
