import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Container, Grid, Card, CardContent, CardMedia, Typography, Box, Rating, IconButton } from '@mui/material';
import { Favorite } from '@mui/icons-material'; // אייקון לייקים

const CategoryRecipe = () => {
  const { categoryId } = useParams(); // קבלת ה- categoryId מתוך ה-URL
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // בקשה לשרת כדי לקבל את המתכונים לפי categoryId
    axios.get(`http://localhost:3000/recipes/category/${categoryId}`)
      .then(response => {
        setRecipes(response.data); // עדכון הנתונים של המתכונים
        setLoading(false); // סיום טעינה
      })
      .catch(err => {
        console.error(err); // טיפול בשגיאות
        setLoading(false); // סיום טעינה גם במקרה של שגיאה
      });
  }, [categoryId]); // התלוות ל- categoryId

  if (loading) {
    return <div>טוען...</div>;
  }

  return (
    <Container>
      <Typography variant="h3" gutterBottom align="center">
        מתכונים בקטגוריה
      </Typography>

      <Grid container spacing={4} justifyContent="center">
        {recipes.length === 0 ? (
          <Typography variant="h6" gutterBottom align="center">
            לא נמצאו מתכונים בקטגוריה הזו
          </Typography>
        ) : (
          recipes.map(recipe => (
            <Grid item xs={12} sm={6} md={4} key={recipe._id}>
              <Card sx={{ display: 'flex', marginBottom: 4 }}>
                {/* צד שמאל: מידע על המתכון */}
                <Box sx={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', padding: 2, flex: 1 }}>
                  <Typography variant="h5" gutterBottom>
                    {recipe.name}
                  </Typography>

                  {/* דרוג כוכבים */}
                  <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    <Rating
                      name="recipe-rating"
                      value={recipe.rating || 0} // אם יש דרוג למתכון
                      precision={0.5}
                      readOnly
                    />
                    <Typography variant="body2" sx={{ marginLeft: 1 }}>
                      ({recipe.rating || 0})
                    </Typography>
                  </Box>

                  {/* לייקים */}
                  <Box sx={{ display: 'flex', alignItems: 'center', marginTop: 2 }}>
                    <IconButton aria-label="add to favorites">
                      <Favorite sx={{ color: recipe.likes > 0 ? 'red' : 'gray' }} />
                    </IconButton>
                    <Typography variant="body1">{recipe.likes} Likes</Typography>
                  </Box>
                </Box>

                {/* צד ימין: התמונה של המתכון */}
                <CardMedia
                  component="img"
                  sx={{ width: 250, height: 'auto' }} // אפשר לשנות את הגודל לפי הצורך
                  image={`http://localhost:3000${recipe.image}`}
                  alt={recipe.name}
                />
              </Card>
            </Grid>
          ))
        )}
      </Grid>
    </Container>
  );
};

export default CategoryRecipe;
