import React, { useState } from "react";
import axios from "axios";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import { useNavigate } from "react-router-dom";
import { styled } from "@mui/material/styles";

const AddCategoryManager = () => {
  const [categoryData, setCategoryData] = useState({
    name: "",
  });
  const [image, setImage] = useState(null);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setCategoryData({
      ...categoryData,
      [e.target.name]: e.target.value,
    });
  };

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!categoryData.name || !image) {
      alert("נא למלא את כל השדות הנדרשים!");
      return;
    }

    const formData = new FormData();
    formData.append("image", image);
    formData.append("name", categoryData.name);

    try {
      const response = await axios.post("http://localhost:3000/categories", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      if (response.status === 200) {
        alert("הקטגוריה נוספה בהצלחה!");
        setCategoryData({ name: "" });
        setImage(null);
        navigate("/categories"); // מפנה לעמוד של הקטגוריות לאחר הוספה
      } else {
        alert("הוספת הקטגוריה נכשלה.");
      }
    } catch (error) {
      console.error("Error adding category:", error);
      alert("אירעה שגיאה במהלך הוספת הקטגוריה.");
    }
  };

  return (
    <Box
      sx={{
        p: 4,
        maxWidth: 600,
        mx: "auto",
        border: "1px solid #e0e0e0",
        borderRadius: 3,
        boxShadow: 3,
        mt: 5,
        backgroundColor: "#fafafa",
        display: "flex",
        flexDirection: "column",
        alignItems: "center", // למרכז את כל התוכן
      }}
    >
      <Typography variant="h4" gutterBottom sx={{ fontWeight: 'bold', color: '#333', textAlign: 'center' }}>
        הוספת קטגוריה חדשה
      </Typography>
      <form onSubmit={handleSubmit} style={{ width: '100%' }}>
        <TextField
          label="שם קטגוריה"
          name="name"
          value={categoryData.name}
          onChange={handleChange}
          fullWidth
          margin="normal"
          variant="outlined"
          sx={{
            borderRadius: '8px',
            backgroundColor: '#fff',
            '& .MuiOutlinedInput-root': {
              '& fieldset': {
                borderColor: '#ccc',
              },
              '&:hover fieldset': {
                borderColor: '#1e88e5',
              },
            },
            marginBottom: "16px",
          }}
        />

        {/* תמונה קטגוריה */}
        <TextField
          label="בחר תמונה של הקטגוריה"
          type="file"
          inputProps={{ accept: "image/*" }}
          onChange={handleImageChange}
          fullWidth
          margin="normal"
          variant="outlined"
          sx={{
            borderRadius: '8px',
            backgroundColor: '#fff',
            '& .MuiOutlinedInput-root': {
              '& fieldset': {
                borderColor: '#ccc',
              },
              '&:hover fieldset': {
                borderColor: '#1e88e5',
              },
            },
            marginBottom: "16px",
          }}
        />

        <Button
          type="submit"
          variant="contained"
          fullWidth
          sx={{
            mt: 3,
            padding: "12px",
            borderRadius: "8px",
            backgroundColor: "#1e88e5",
            color: "#fff",
            '&:hover': {
              backgroundColor: "#1565c0",
            },
            fontSize: '16px', // הגדלת גודל הפונט
            fontWeight: 'bold', // בולד לכפתור
          }}
        >
          הוסף קטגוריה
        </Button>
      </form>
    </Box>
  );
};

export default AddCategoryManager;
