import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Container, Typography, Box, CardMedia } from '@mui/material';

const OneCategory = () => {
  const { id } = useParams(); 
  const [recipe, setRecipe] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:3000/recipes/${id}`)
      .then(response => {
        setRecipe(response.data); 
      })
      .catch(err => {
        console.error("Error fetching recipe:", err);
      });
  }, [id]); 

  if (!recipe) {
    return <div>טוען...</div>; 
  }

  return (
    <Container>
     <CardMedia component="img" height="140" image={`http://localhost:3000${recipe.image}`} alt={recipe.name} />
      <Typography variant="h4" gutterBottom>{recipe.name}</Typography>
      <Box>
        <Typography variant="body1"><strong>זמן הכנה:</strong> {recipe.preparationTime} דקות</Typography>
        <Typography variant="body1"><strong>כמות:</strong> {recipe.finalYield}</Typography>
        <Typography variant="body1"><strong>מצרכים:</strong></Typography>
        <ul>
          {recipe.ingredients.map((ingredient, index) => (
            <li key={index}>{ingredient}</li>
          ))}
        </ul>
        <Typography variant="body1"><strong>שלבי הכנה:</strong></Typography>
        <ol>
          {recipe.preparationSteps.map((step, index) => (
            <li key={index}>{step}</li>
          ))}
        </ol>
      </Box>
    </Container>
  );
};

export default OneCategory;
