import axios from "axios";
import React, { useState } from "react";


const NewAddRecipe = () => {
  const [recipes, setRecipes] = useState(meatRecipes);

  // עדכון שדה התמונה עבור מתכון מסוים
  const handleImageChange = (event, recipeId) => {
    const file = event.target.files[0];
    const updatedRecipes = recipes.map((recipe) => {
      if (recipe.id === recipeId) {
        return { ...recipe, image: URL.createObjectURL(file) }; // עדכון התמונה עבור המתכון
      }
      return recipe;
    });
    setRecipes(updatedRecipes);
  };

  // שליחת כל המתכונים לשרת
  const handleSubmit = async () => {
    for (const recipe of recipes) {
      const updatedRecipe = { ...recipe };
      if (!updatedRecipe.image) {
        alert(`Please add an image for recipe ${updatedRecipe.id}`); // אם לא הוספה תמונה, להפסיק
        return;
      }

      try {
        const response = await axios.post("http://localhost:3000/recipes", updatedRecipe, {
          headers: {
            "Content-Type": "application/json",
          },
        });
        console.log(`Recipe added: ${recipe.image}`, response.data);
      } catch (error) {
        console.error(`Error adding recipe ${recipe.id}:`, error);
      }
    }
  };

  return (
    <div>
      <h2>Add Recipes</h2>
      <div>
        {recipes.map((recipe, index) => (
          <div key={index} style={{ marginBottom: "20px" }}>
            <h3>Recipe {recipe.id}</h3>
            <p>Publish Date: {recipe.publishDate}</p>
            <p>Preparation Time: {recipe.preparationTime} minutes</p>
            <p>Final Yield: {recipe.finalYield}</p>
            <p>Likes: {recipe.likes}</p>
            <input
              type="file"
              onChange={(e) => handleImageChange(e, recipe.id)}
              accept="image/*"
            />
            {recipe.image && <img src={recipe.image} alt="Recipe" width="100" />}
          </div>
        ))}
      </div>
      <button onClick={handleSubmit}>Submit All Recipes</button>
    </div>
  );
};

export default NewAddRecipe;
