import axios from "axios";
import React, { useState } from "react";
const meatRecipes = [
  {
    image: "/uploads/steak.jpeg",
    publishDate: "2025-01-22",
    categoryCode: "679215da728744aafe74dfc4",
    preparationTime: 30,
    ingredients: ["בשר בקר", "שמן זית", "מלח", "פלפל שחור"],
    preparationSteps: ["לחמם מחבת עם שמן זית", "לתבל את הבשר", "לצרוב 3 דקות מכל צד"],
    finalYield: "2 מנות",
    likes: 25,
  },
  {
    image: "/uploads/kebab.jpeg",
    publishDate: "2025-01-23",
    categoryCode: "679215da728744aafe74dfc4",
    preparationTime: 40,
    ingredients: ["בשר טחון", "פטרוזיליה", "בצל", "תבלינים"],
    preparationSteps: ["לקצוץ בצל ופטרוזיליה", "לערבב עם הבשר והתבלינים", "לעצב לקבבים ולאפות"],
    finalYield: "6 מנות",
    likes: 40,
  },
  {
    image: "/uploads/shawarma.jpeg",
    publishDate: "2025-01-24",
    categoryCode: "679215da728744aafe74dfc4",
    preparationTime: 50,
    ingredients: ["שווארמה הודו", "תבלינים", "פיתה", "ירקות"],
    preparationSteps: ["לבשל את השווארמה עם תבלינים", "להגיש בפיתה עם ירקות"],
    finalYield: "4 מנות",
    likes: 55,
  },
  {
    image: "/uploads/burger.jpeg",
    publishDate: "2025-01-25",
    categoryCode: "679215da728744aafe74dfc4",
    preparationTime: 35,
    ingredients: ["בשר טחון", "לחמנייה", "עגבנייה", "חסה"],
    preparationSteps: ["להכין קציצת בשר", "לטגן ולהרכיב המבורגר בלחמנייה"],
    finalYield: "2 מנות",
    likes: 70,
  },
  {
    image: "/uploads/goulash.jpeg",
    publishDate: "2025-01-26",
    categoryCode: "679215da728744aafe74dfc4",
    preparationTime: 90,
    ingredients: ["בשר בקר", "בצל", "גזר", "פפריקה"],
    preparationSteps: ["לטגן בצל", "להוסיף בשר ותבלינים", "לבשל על אש קטנה"],
    finalYield: "6 מנות",
    likes: 60,
  },
  {
    image: "/uploads/kofta.jpeg",
    publishDate: "2025-01-27",
    categoryCode: "679215da728744aafe74dfc4",
    preparationTime: 45,
    ingredients: ["בשר טחון", "שום", "כמון", "פטרוזיליה"],
    preparationSteps: ["לערבב מרכיבים", "להכין קציצות ולצלות בתנור"],
    finalYield: "4 מנות",
    likes: 35,
  },
  {
    image: "/uploads/steak_and_potatoes.jpeg",
    publishDate: "2025-01-28",
    categoryCode: "679215da728744aafe74dfc4",
    preparationTime: 60,
    ingredients: ["סטייק אנטריקוט", "תפוחי אדמה", "שום", "רוזמרין"],
    preparationSteps: ["לתבל את הסטייק", "לאפות תפוחי אדמה בתנור"],
    finalYield: "3 מנות",
    likes: 80,
  },
  {
    image: "/uploads/meatballs.jpeg",
    publishDate: "2025-01-29",
    categoryCode: "679215da728744aafe74dfc4",
    preparationTime: 75,
    ingredients: ["בשר טחון", "רסק עגבניות", "בצל", "תבלינים"],
    preparationSteps: ["לערבב בשר ותבלינים", "לבשל ברוטב עגבניות"],
    finalYield: "5 מנות",
    likes: 50,
  },
];


const AddRecipeForm = () => {
  const [recipeData, setRecipeData] = useState({
    publishDate: "",
    categoryCode: "",
    preparationTime: "",
    ingredients: [],
    preparationSteps: [],
    finalYield: "",
    likes: "",
  });

  const [image, setImage] = useState(null);
  const [ingredientInput, setIngredientInput] = useState("");
  const [stepInput, setStepInput] = useState("");

  const handleChange = (e) => {
    setRecipeData({
      ...recipeData,
      [e.target.name]: e.target.value,
    });
  };

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  const addIngredient = () => {
    if (ingredientInput.trim()) {
      setRecipeData({
        ...recipeData,
        ingredients: [...recipeData.ingredients, ingredientInput.trim()],
      });
      setIngredientInput("");
    }
  };

  const addStep = () => {
    if (stepInput.trim()) {
      setRecipeData({
        ...recipeData,
        preparationSteps: [...recipeData.preparationSteps, stepInput.trim()],
      });
      setStepInput("");
    }
  };
  const handleSubmit = async (e) => {
    e.preventDefault();

    // יצירת FormData
    // const formData = new FormData();
    // formData.append("image", image); // ודא ששדה זה מוגדר כ"image"
    // formData.append("publishDate", recipeData.publishDate);
    // formData.append("categoryCode", recipeData.categoryCode);
    // formData.append("preparationTime", recipeData.preparationTime);
    // formData.append("ingredients", JSON.stringify(recipeData.ingredients));
    // formData.append("preparationSteps", JSON.stringify(recipeData.preparationSteps));
    // formData.append("finalYield", recipeData.finalYield);
    // formData.append("likes", recipeData.likes);

    const formData = new FormData();

    formData.append("image", image); // ודא ששדה זה מוגדר כ"image"
    formData.append("publishDate", meatRecipes[7].publishDate);
    formData.append("categoryCode", meatRecipes[7].categoryCode);
    formData.append("preparationTime", meatRecipes[7].preparationTime);
    formData.append("ingredients", JSON.stringify(meatRecipes[7].ingredients));
    formData.append("preparationSteps", JSON.stringify(meatRecipes[7].preparationSteps));
    formData.append("finalYield", meatRecipes[7].finalYield);
    formData.append("likes", meatRecipes[7].likes);

    // הדפסת הנתונים לבדיקה
    for (let pair of formData.entries()) {
      console.log(pair[0], pair[1]); // זה צריך להדפיס את שם השדה והערך, כולל תמונה
    }

    try {
      formData.forEach(data => console.log(data)
      )
      console.log('form-data', formData);

      const response = await axios.post("http://localhost:3000/recipes", formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      const result = await response.data;
      if (response.status === 200) {
        alert("Recipe added successfully!");
        setRecipeData({
          publishDate: "",
          categoryCode: "",
          preparationTime: "",
          ingredients: [],
          preparationSteps: [],
          finalYield: "",
          likes: "",
        });
        setImage(null);
      } else {
        alert("Failed to add recipe: " + result.error);
      }
    } catch (error) {
      console.error("Error adding recipe:", error);
      alert("An error occurred while adding the recipe.");
    }
  };

  return (
    <div>
      <h2>Add New Recipe</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <div>
          <label>Recipe Image:</label>
          <input type="file" accept="image/*" onChange={handleImageChange} required />
        </div>
        <div>
          <label>Publish Date:</label>
          <input type="date" name="publishDate" value={recipeData.publishDate} onChange={handleChange}  />
        </div>
        <div>
          <label>Category Code:</label>
          <input type="text" name="categoryCode" value={recipeData.categoryCode} onChange={handleChange}  />
        </div>
        <div>
          <label>Preparation Time (minutes):</label>
          <input type="number" name="preparationTime" value={recipeData.preparationTime} onChange={handleChange}  />
        </div>
        <div>
          <label>Ingredients:</label>
          <input type="text" value={ingredientInput} onChange={(e) => setIngredientInput(e.target.value)} />
          <button type="button" onClick={addIngredient}>Add Ingredient</button>
          <ul>
            {recipeData.ingredients.map((ing, index) => (
              <li key={index}>{ing}</li>
            ))}
          </ul>
        </div>
        <div>
          <label>Preparation Steps:</label>
          <input type="text" value={stepInput} onChange={(e) => setStepInput(e.target.value)} />
          <button type="button" onClick={addStep}>Add Step</button>
          <ul>
            {recipeData.preparationSteps.map((step, index) => (
              <li key={index}>{step}</li>
            ))}
          </ul>
        </div>
        <div>
          <label>Final Yield:</label>
          <input type="text" name="finalYield" value={recipeData.finalYield} onChange={handleChange}  />
        </div>
        <div>
          <label>Likes:</label>
          <input type="number" name="likes" value={recipeData.likes} onChange={handleChange} />
        </div>
        <button type="submit">Add Recipe</button>
      </form>
    </div>
  );
};

export default AddRecipeForm;
