import React, { useState, useEffect } from "react";
import axios from "axios";

const RecipeList = () => {
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    // קריאת נתונים מהשרת
    const fetchRecipes = async () => {
      try {
        const response = await axios.get("http://localhost:3000/recipes");
        setRecipes(response.data); // עדכון סטייט עם המתכונים שהתקבלו
      } catch (error) {
        console.error("Error fetching recipes:", error);
      }
    };

    fetchRecipes(); // קריאת המתכונים עם טעינת הקומפוננטה
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h2>All Recipes</h2>
      <div className="recipe-list" style={{ display: "grid", gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))", gap: "20px" }}>
        {recipes.map((recipe) => (
          <div key={recipe._id} style={{ border: "1px solid #ccc", borderRadius: "8px", padding: "10px", boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)" }}>
            <div style={{ textAlign: "center" }}>
              <img
                src={`http://localhost:3000${recipe.image}`} // הגישה לתמונה בשרת
                alt={recipe.name}
                style={{ width: "100%", height: "200px", objectFit: "cover", borderRadius: "8px" }}
              />
            </div>
            <h3 style={{ textAlign: "center", marginTop: "10px" }}>{recipe.name}</h3>
            <p>{recipe.publishDate}</p>
            <p><strong>Preparation Time:</strong> {recipe.preparationTime} minutes</p>
            <p><strong>Final Yield:</strong> {recipe.finalYield}</p>
            <p><strong>Likes:</strong> {recipe.likes}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RecipeList;
