import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Container, Grid, Typography, Card, CardContent, CardMedia, Box, Paper } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const [categories, setCategories] = useState([]);
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);

  const navigate = useNavigate();

  useEffect(() => {
    // בקשה לשרת כדי לקבל את כל הקטגוריות
    axios.get('http://localhost:3000/categories')
      .then(response => {
        setCategories(response.data);
      })
      .catch(err => console.error(err));

    // בקשה לשרת כדי לקבל את המתכונים הפופולריים (נניח שהנתיב הוא /recipes/popular)
    axios.get('http://localhost:3000/recipes/popular')
      .then(response => {
        setRecipes(response.data);
      })
      .catch(err => console.error(err))
      .finally(() => setLoading(false));
  }, []);

  if (loading) {
    return <div>טוען...</div>;
  }

  return (
    <Container>
      {/* כותרת הדף */}
      <Typography variant="h3" gutterBottom align="center">
        <Card style={{ position: 'relative', margin: '20px' }}>
          <CardMedia
            component="img"
            height="270"
            image={process.env.PUBLIC_URL + "888-10.jpg"}
            style={{ width: '100%' }}
          />
          <Box
            style={{
              position: 'absolute',
              top: '40%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
              color: 'white',
              fontSize: '50px',
              fontWeight: 'bold',
              padding: '10px',
              zIndex: 1
            }}
          >
            מתכונים שמחממים את הלב
          </Box>
        </Card>
      </Typography>

      {/* הצגת קטגוריות */}
      <Typography variant="h4" gutterBottom style={{ textAlign: "center" }}>
        קטגוריות
      </Typography>
      <Grid container spacing={4} justifyContent="center">
        {categories.map(category => (
          <Grid item xs={12} sm={6} md={6} key={category._id}>
            <Card
              onClick={() => navigate(`/category-recipe/${category._id}`)}
              style={{
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                height: 'auto',
                transition: 'transform 0.3s ease', // אפקט החלקה לריחוף
                boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', // הוספתי צל קטן
                cursor: 'pointer', // הצבע את הסמן כמצביע
              }}
              // אפקט ריחוף שיגדיל את כל הכרטיס ב-10% במקום 20%
              onMouseEnter={(e) => e.currentTarget.style.transform = 'scale(1.1)'} // הגדלה ב-10%
              onMouseLeave={(e) => e.currentTarget.style.transform = 'scale(1)'}
            >
              <CardMedia
                component="img"
                height="140"
                image={`http://localhost:3000${category.image}`}
                alt={category.name}
              />
              <CardContent style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                <Typography variant="h6" gutterBottom style={{ textAlign: 'center' }}>
                  {category.name}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* ציטוטים מלקוחות */}
      <Box my={6}>
        <Typography variant="h4" gutterBottom align="center">
          מה הלקוחות שלנו אומרים
        </Typography>
        <Grid container spacing={4} justifyContent="center">
          {['"מתכונים מדהימים, אהבתי כל מנה!"', '"שירות נהדר ומאכלים טעימים!"', '"מקום מושלם לאוהבי אוכל!"'].map((quote, index) => (
            <Grid item xs={12} sm={4} key={index}>
              <Paper elevation={3} sx={{ padding: 4, textAlign: 'center' }}>
                <Typography variant="h6" color="textSecondary">
                  {quote}
                </Typography>
              </Paper>
            </Grid>
          ))}
        </Grid>
      </Box>

      {/* תמונות של המתכונים הפופולריים */}
      <Typography variant="h4" gutterBottom align="center">
        מתכונים פופולריים
      </Typography>
      <Grid container spacing={4} justifyContent="center">
        {recipes.map(recipe => (
          <Grid item xs={12} sm={6} md={6} key={recipe._id}>
            <Card>
              <CardMedia
                component="img"
                height="140"
                image={`http://localhost:3000${recipe.image}`}
                alt={recipe.name}
              />
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  {recipe.name}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default Home;
