import React, { useState } from "react";
import axios from "axios";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import { useNavigate } from "react-router-dom";
import { Snackbar, Alert } from "@mui/material"; // מייבא את Snackbar ו-Alert של MUI

const AddRecipeManager = () => {
  const [recipeData, setRecipeData] = useState({
    name: "",
    description: "",
  });
  const [image, setImage] = useState(null);
  const [openSnackbar, setOpenSnackbar] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState("");
  const [snackbarSeverity, setSnackbarSeverity] = useState("success"); // הצלחה או שגיאה
  const navigate = useNavigate();

  const handleChange = (e) => {
    setRecipeData({
      ...recipeData,
      [e.target.name]: e.target.value,
    });
  };

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!recipeData.name || !image || !recipeData.description) {
      setSnackbarMessage("נא למלא את כל השדות הנדרשים!");
      setSnackbarSeverity("error");
      setOpenSnackbar(true);
      return;
    }

    const formData = new FormData();
    formData.append("image", image);
    formData.append("name", recipeData.name);
    formData.append("description", recipeData.description);

    try {
      const response = await axios.post("http://localhost:3000/recipes", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      if (response.status === 200) {
        setSnackbarMessage("המתכון נוספה בהצלחה!");
        setSnackbarSeverity("success");
        setOpenSnackbar(true);
        setRecipeData({ name: "", description: "" });
        setImage(null);
        navigate("/recipes"); // מפנה לעמוד המתכונים לאחר הוספה
      } else {
        setSnackbarMessage("הוספת המתכון נכשלה.");
        setSnackbarSeverity("error");
        setOpenSnackbar(true);
      }
    } catch (error) {
      console.error("Error adding recipe:", error);
      setSnackbarMessage("אירעה שגיאה במהלך הוספת המתכון.");
      setSnackbarSeverity("error");
      setOpenSnackbar(true);
    }
  };

  return (
    <Box
      sx={{
        p: 4,
        maxWidth: 600,
        mx: "auto",
        border: "1px solid #e0e0e0",
        borderRadius: 3,
        boxShadow: 3,
        mt: 5,
        backgroundColor: "#fafafa",
        display: "flex",
        flexDirection: "column",
        alignItems: "center", // למרכז את כל התוכן
        position: "relative", // זה עוזר לנו למקם את הטופס בתחתית
      }}
    >
      <Typography variant="h4" gutterBottom sx={{ fontWeight: 'bold', color: '#333', textAlign: 'center' }}>
        הוספת מתכון חדש
      </Typography>
      <form onSubmit={handleSubmit} style={{ width: '100%' }}>
        <TextField
          label="שם המתכון"
          name="name"
          value={recipeData.name}
          onChange={handleChange}
          fullWidth
          margin="normal"
          variant="outlined"
          sx={{
            borderRadius: '8px',
            backgroundColor: '#fff',
            '& .MuiOutlinedInput-root': {
              '& fieldset': {
                borderColor: '#ccc',
              },
              '&:hover fieldset': {
                borderColor: '#1e88e5',
              },
            },
            marginBottom: "16px",
          }}
        />

        {/* תיאור המתכון */}
        <TextField
          label="תיאור המתכון"
          name="description"
          value={recipeData.description}
          onChange={handleChange}
          fullWidth
          margin="normal"
          variant="outlined"
          sx={{
            borderRadius: '8px',
            backgroundColor: '#fff',
            '& .MuiOutlinedInput-root': {
              '& fieldset': {
                borderColor: '#ccc',
              },
              '&:hover fieldset': {
                borderColor: '#1e88e5',
              },
            },
            marginBottom: "16px",
          }}
        />

        {/* תמונה המתכון */}
        <TextField
          label="בחר תמונה של המתכון"
          type="file"
          inputProps={{ accept: "image/*" }}
          onChange={handleImageChange}
          fullWidth
          margin="normal"
          variant="outlined"
          sx={{
            borderRadius: '8px',
            backgroundColor: '#fff',
            '& .MuiOutlinedInput-root': {
              '& fieldset': {
                borderColor: '#ccc',
              },
              '&:hover fieldset': {
                borderColor: '#1e88e5',
              },
            },
            marginBottom: "16px",
          }}
        />

        <Button
          type="submit"
          variant="contained"
          fullWidth
          sx={{
            mt: 3,
            padding: "12px",
            borderRadius: "8px",
            backgroundColor: "#1e88e5",
            color: "#fff",
            '&:hover': {
              backgroundColor: "#1565c0",
            },
            fontSize: '16px', // הגדלת גודל הפונט
            fontWeight: 'bold', // בולד לכפתור
          }}
        >
          הוסף מתכון
        </Button>
      </form>

      {/* Snackbar להודעות */}
      <Snackbar
        open={openSnackbar}
        autoHideDuration={6000} // הזמן שההודעה תישאר
        onClose={() => setOpenSnackbar(false)}
      >
        <Alert onClose={() => setOpenSnackbar(false)} severity={snackbarSeverity} sx={{ width: '100%' }}>
          {snackbarMessage}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default AddRecipeManager;
