import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Container, Typography, Box, CardMedia, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, List, ListItem, ListItemText } from '@mui/material';

const OneCategory = () => {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null);
  const [rsponse, setRsponse] = useState(null);


  useEffect(() => {
    //////////////////////
    axios.get(`http://localhost:3000/recipes/${id}`)
      .then(response => {
        setRecipe(response.data);
      })
      .catch(err => {
        console.error("Error fetching recipe:", err);
      });
    ///////////////////
    axios.get(`http://localhost:3000/response/${id}`)
      .then(response => {
        setRsponse(response.data);
      })
      .catch(err => {
        console.error("Error fetching rsponse:", err);
      });
  }, [id]);

  if (!recipe) {
    return <div>◊ò◊ï◊¢◊ü...</div>;
  }
  if (!recipe) {
    return <div>◊ò◊ï◊¢◊ü...</div>;
  }

  return (
    <Container sx={{ mt: 4, paddingTop: "50px" }}>
      <Typography
        variant="h3"
        align="center"
        gutterBottom
        sx={{
          mt: 2,
          fontWeight: 'bold',
          textDecoration: 'underline',
          fontFamily: '"Assistant", sans-serif'
        }}
      >
        {recipe.name}
      </Typography>

      <Box sx={{ display: 'flex', justifyContent: 'center', gap: 4, mt: 2 }}>
        <Typography variant="h6" sx={{ fontFamily: '"Varela Round", sans-serif', fontWeight: 400 }}>
          <strong>‚è≥ ◊ñ◊û◊ü ◊î◊õ◊†◊î:</strong> {recipe.preparationTime} ◊ì◊ß◊ï◊™
        </Typography>
        <Typography variant="h6" sx={{ fontFamily: '"Varela Round", sans-serif', fontWeight: 400 }}>
          <strong>üçΩ ◊õ◊û◊ï◊™:</strong> {recipe.finalYield}
        </Typography>
      </Box>

      <CardMedia
        component="img"
        image={`http://localhost:3000${recipe.image}`}
        alt={recipe.name}
        sx={{
          width: "100%",
          maxWidth: 500,
          height: 300,
          borderRadius: 2,
          boxShadow: 3,
          objectFit: "cover",
          mx: "auto",
          display: "block"
        }}
      />

      {/* ◊ò◊ë◊ú◊™ ◊û◊¶◊®◊õ◊ô◊ù */}
      <Box sx={{ mt: 4 }}>
        <Typography
          variant="h5"
          gutterBottom
          sx={{
            fontWeight: 'bold',
            textAlign: 'center',
            bgcolor: '#f5f5f5',
            py: 1,
            borderRadius: 1,
            fontFamily: '"Assistant", sans-serif'
          }}
        >
          ◊û◊¶◊®◊õ◊ô◊ù
        </Typography>
        <TableContainer component={Paper} sx={{ maxWidth: 500, mx: 'auto' }}>
          <Table>
            <TableHead>
              <TableRow sx={{ bgcolor: '#ddd' }}>
                <TableCell align="center" sx={{ fontWeight: 'bold' }}>◊õ◊û◊ï◊™</TableCell>
                <TableCell align="center" sx={{ fontWeight: 'bold' }}>◊û◊¶◊®◊ö</TableCell>
              </TableRow>
            </TableHead>
            <TableBody >
              {recipe.ingredients.map((ingredient, index) => {
                const [amount, name] = ingredient.split(' ', 2); // ◊û◊§◊®◊ô◊ì ◊õ◊û◊ï◊™ ◊ï◊©◊ù
                return (
                  <TableRow key={index}>
                    <TableCell align="center">{amount || '-'}</TableCell>
                    <TableCell align="center">{name || ingredient}</TableCell>
                  </TableRow>
                );
              })}
            </TableBody>
          </Table>
        </TableContainer>
      </Box>


      <Box sx={{ mt: 4 }}>
        <Typography
          variant="h5"
          gutterBottom
          sx={{
            fontWeight: 'bold',
            textAlign: 'center',
            bgcolor: '#f5f5f5',
            py: 1,
            borderRadius: 1,
            fontFamily: '"Assistant", sans-serif'
          }}
        >
          ◊©◊ú◊ë◊ô ◊î◊õ◊†◊î
        </Typography >
        <Paper elevation={3} sx={{ p: 2, mt: 2 }}>
          <List >
            {recipe.preparationSteps.map((step, index) => (
              <ListItem sx={{ textAlign: 'start' }} key={index}>
                <ListItemText
                  primary={` ${step}`}
                  sx={{ fontFamily: '"Varela Round", sans-serif', direction: "rtl" }}
                />
              </ListItem>
            ))}
          </List>
        </Paper>
      </Box>

      <Box sx={{ mt: 4 }}>
        <Typography
          variant="h5"
          gutterBottom
          sx={{
            fontWeight: 'bold',
            textAlign: 'center',
            bgcolor: '#f5f5f5',
            py: 1,
            borderRadius: 1,
            fontFamily: '"Assistant", sans-serif'
          }}
        >
          ◊™◊í◊ï◊ë◊ï◊™ ◊¢◊ú ◊î◊û◊™◊õ◊ï◊ü
        </Typography>
      </Box>
    </Container>
  );
};

export default OneCategory;
