import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import { useNavigate } from "react-router-dom";
import { Box, Typography } from "@mui/material";

const UpdateUser = () => {
  const [userData, setUserData] = useState({
    username: "",
    password: "",
    email: "", // המייל יהיה כתוב אבל לא ניתן לשנות אותו
  });
  const [errors, setErrors] = useState({
    username: "",
    password: "",
  });
  const navigate = useNavigate();
  const userId = useContext(user); // תוכל להמיר ל-ID האמיתי של המשתמש או להעביר אותו כפרופס

  // טעינת המידע של המשתמש כשנטען הרכיב
  useEffect(() => {
    const getUserData = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/users/${userId}`);
        setUserData({
          username: response.data.username,
          password: "", // מוודא שהמשתמש לא יראה את הסיסמה
          email: response.data.email,
        });
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };

    getUserData();
  }, [userId]);

  const handleChange = (e) => {
    setUserData({
      ...userData,
      [e.target.name]: e.target.value,
    });
  };

  const validateForm = () => {
    const newErrors = { username: "", password: "" };
    if (userData.username.length < 3) {
      newErrors.username = "שם המשתמש חייב להיות באורך מינימלי של 3 תוים";
    }
    if (userData.password.length < 6) {
      newErrors.password = "הסיסמה חייבת להיות באורך מינימלי של 6 תוים";
    }
    setErrors(newErrors);

    return !newErrors.username && !newErrors.password;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    try {
      const updatedUser = {
        username: userData.username,
        password: userData.password,
        // לא שולחים את המייל כי הוא לא ניתן לשינוי
      };

      const response = await axios.put(`http://localhost:3000/users/${userId}`, updatedUser);

      if (response.status === 200) {
        alert("המשתמש עודכן בהצלחה");
        navigate("/home"); // לדף הפרופיל או דף אחר
      }
    } catch (error) {
      console.error("Error updating user:", error);
      alert("אירעה שגיאה בעת עדכון המשתמש");
    }
  };

  return (
    <Box
      sx={{
        maxWidth: 500,
        mx: "auto",
        mt: 5,
        padding: 3,
        border: "1px solid #ccc",
        borderRadius: 2,
      }}
    >
      <Typography variant="h4" gutterBottom sx={{ textAlign: "center" }}>
        עדכון פרטי משתמש
      </Typography>

      <form onSubmit={handleSubmit}>
        <TextField
          label="שם משתמש"
          name="username"
          value={userData.username}
          onChange={handleChange}
          fullWidth
          margin="normal"
          error={Boolean(errors.username)}
          helperText={errors.username}
        />

        <TextField
          label="סיסמה"
          name="password"
          value={userData.password}
          onChange={handleChange}
          type="password"
          fullWidth
          margin="normal"
          error={Boolean(errors.password)}
          helperText={errors.password}
        />

        {/* שדה המייל, לא ניתן לשנות אותו */}
        <TextField
          label="מייל"
          value={userData.email}
          fullWidth
          margin="normal"
          InputProps={{
            readOnly: true,
          }}
        />

        <Button type="submit" variant="contained" fullWidth sx={{ mt: 3 }}>
          עדכן משתמש
        </Button>
      </form>
    </Box>
  );
};

export default UpdateUser;
