import axios from "axios";
import React, { useEffect } from "react";

const recipes = [
  {
    image: "https://example.com/image1.jpg",
    publishDate: "2024-01-15",
    categoryCode: "679215da728744aafe74dfc4",
    preparationTime: 45,
    ingredients: ["flour", "sugar", "eggs"],
    preparationSteps: ["Mix ingredients", "Bake at 180C"],
    finalYield: "10 servings",
    likes: 150,
  },
  {
    image: "https://example.com/image2.jpg",
    publishDate: "2024-01-10",
    categoryCode: "679215da728744aafe74dfc4",
    preparationTime: 60,
    ingredients: ["chicken", "spices", "vegetables"],
    preparationSteps: ["Marinate chicken", "Cook for 40 minutes"],
    finalYield: "4 servings",
    likes: 200,
  },
];

const NewAddRecipe = () => {
  useEffect(() => {
    const addRecipes = async () => {
      for (const recipe of recipes) {
        try {
          const response = await axios.post("http://localhost:3000/recipes", recipe, {
            headers: {
              "Content-Type": "application/json",
            },
          });
          console.log(`Recipe added: ${recipe.categoryCode}`, response.data);
        } catch (error) {
          console.error(`Error adding recipe ${recipe.categoryCode}:`, error);
        }
      }
    };

    addRecipes();
  }, []);

  return (
    <div>
      <h2>Adding Recipes...</h2>
    </div>
  );
};

export default NewAddRecipe;
