import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Container, Typography, Box, CardMedia, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, List, ListItem, ListItemText, TextField, IconButton } from '@mui/material';
import BorderColorIcon from '@mui/icons-material/BorderColor';
const OneCategory = () => {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null);
  const [rsponse, setRsponse] = useState(null);
  const [newResponse, setNewResponse] = useState('');
  const [showInput, setShowInput] = useState(false);
  const { user } = useContext(UserContext);

  useEffect(() => {
    // קריאת המתכון
    axios.get(`http://localhost:3000/recipes/${id}`)
      .then(response => {
        setRecipe(response.data);
      })
      .catch(err => {
        console.error("Error fetching recipe:", err);
      });

    // קריאת התגובות
    axios.get(`http://localhost:3000/response/${id}`)
      .then(response => {
        setRsponse(response.data);
      })
      .catch(err => {
        console.error("Error fetching responses:", err);
      });
  }, [id]);

  // פונקציה להוספת תגובה
  const addResponse = () => {
    const responseData = {
      userCode: { username: "שם המשתמש" }, // כאן תוכל להוסיף את שם המשתמש (אם יש לך מידע על כך)
      content: newResponse
    };

    axios.post(`http://localhost:3000/response/${id}`, responseData)
      .then(response => {
        setRsponse(prevResponses => [...prevResponses, response.data]);  // עדכון רשימת התגובות
        setNewResponse('');  // איפוס אינפוט התגובה
        setShowInput(false);  // סגירת האינפוט
      })
      .catch(err => {
        console.error("Error adding response:", err);
      });
  };

  if (!recipe || !rsponse) {
    return <div>טוען...</div>;
  }

  return (
    <Container sx={{ mt: 4, paddingTop: "50px" }}>
      <Typography variant="h3" align="center" gutterBottom sx={{ mt: 2, fontWeight: 'bold', textDecoration: 'underline', fontFamily: '"Assistant", sans-serif' }}>
        {recipe.name}
      </Typography>


      <CardMedia
        component="img"
        image={`http://localhost:3000${recipe.image}`}
        alt={recipe.name}
        sx={{
          width: "100%",
          maxWidth: 500,
          height: 300,
          borderRadius: 2,
          boxShadow: 3,
          objectFit: "cover",
          mx: "auto",
          display: "block"
        }}
      />


      <Box sx={{ mt: 4 }}>
        <Typography variant="h5" gutterBottom sx={{ fontWeight: 'bold', textAlign: 'center', bgcolor: '#f5f5f5', py: 1, borderRadius: 1, fontFamily: '"Assistant", sans-serif' }}>
          מצרכים
        </Typography>
        <TableContainer component={Paper} sx={{ maxWidth: 500, mx: 'auto' }}>
          <Table>
            <TableHead>
              <TableRow sx={{ bgcolor: '#ddd' }}>
                <TableCell align="center" sx={{ fontWeight: 'bold' }}>כמות</TableCell>
                <TableCell align="center" sx={{ fontWeight: 'bold' }}>מצרך</TableCell>
              </TableRow>
            </TableHead>
            <TableBody >
              {recipe.ingredients.map((ingredient, index) => {
                const [amount, name] = ingredient.split(' ', 2); // מפריד כמות ושם
                return (
                  <TableRow key={index}>
                    <TableCell align="center">{amount || '-'}</TableCell>
                    <TableCell align="center">{name || ingredient}</TableCell>
                  </TableRow>
                );
              })}
            </TableBody>
          </Table>
        </TableContainer>
      </Box>


      <Box sx={{ mt: 4 }}>
        <Typography variant="h5" gutterBottom sx={{ fontWeight: 'bold', textAlign: 'center', bgcolor: '#f5f5f5', py: 1, borderRadius: 1, fontFamily: '"Assistant", sans-serif' }}>
          שלבי הכנה
        </Typography>
        <Paper elevation={3} sx={{ p: 2, mt: 2 }}>
          <List>
            {recipe.preparationSteps.map((step, index) => (
              <ListItem sx={{ textAlign: 'start' }} key={index}>
                <ListItemText primary={` ${step}`} sx={{ fontFamily: '"Varela Round", sans-serif', direction: "rtl" }} />
              </ListItem>
            ))}
          </List>
        </Paper>
      </Box>


      <Box sx={{ mt: 4 }}>
        <Typography variant="h5" gutterBottom sx={{ fontWeight: 'bold', textAlign: 'center', bgcolor: '#f5f5f5', py: 1, borderRadius: 1, fontFamily: '"Assistant", sans-serif' }}>
          תגובות על המתכון
        </Typography>
        <Paper elevation={3} sx={{ p: 2, mt: 2 }}>
          <List>
            {rsponse.map((response, index) => (
              <ListItem sx={{ textAlign: 'start' }} key={index}>
                <ListItemText primary={` ${response.content} (${response?.userCode?.username})`} sx={{ fontFamily: '"Varela Round", sans-serif', direction: "rtl" }} />
              </ListItem>
            ))}


        <Box sx={{ direction:"rtl",display: 'flex', justifyContent: 'space-between', alignItems: 'center', mt: 2 }}>
          {showInput && (
            <TextField
            color='black'
              label="הוסף תגובה"
              variant="outlined"
              fullWidth
              value={newResponse}
              onChange={(e) => setNewResponse(e.target.value)}
              sx={{ marginRight:2,direction:"rtl"}}
            />
          )}
          <IconButton onClick={() => setShowInput(!showInput)} color="black">
            <BorderColorIcon sx={{ fontSize: 40 }} />
          </IconButton>
          {showInput && (
            <IconButton onClick={addResponse} color="black">
             שלח
            </IconButton>
          )}

        </Box>
        </List>
        </Paper>
      </Box>

    </Container>
  );
};

export default OneCategory;
