import React, { useContext, useState } from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import Menu from '@mui/material/Menu';
import MenuIcon from '@mui/icons-material/Menu';
import Container from '@mui/material/Container';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import Tooltip from '@mui/material/Tooltip';
import MenuItem from '@mui/material/MenuItem';
import CottageIcon from '@mui/icons-material/Cottage';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';
import { UserContext } from './Context';
import InputBase from '@mui/material/InputBase';
import SearchIcon from '@mui/icons-material/Search';

const NavBar = () => {
  const { user } = useContext(UserContext);
  console.log('user', user);

  const userpages = [
    { name: "כל המתכונים", route: 'home' },
    { name: "אודות", route: 'about' },
    { name: "התחברות", route: 'login' },
    { name: "הרשמה", route: 'singup' }
  ];

  const pages = user && user.isAdmin
    ? [
      { name: "כל המתכונים", route: 'home' },
      { name: "אודות", route: 'about' },
      { name: "התחברות", route: 'login' },
      { name: "הרשמה", route: 'singup' },
      { name: "הוספת קטגוריה", route: 'AddCategoryManager' },
      { name: "הוספת מתכון", route: 'AddRecipeMeneger' }
    ]
    : [
      { name: "כל המתכונים", route: 'home' },
      { name: "אודות", route: 'about' },
      { name: "התחברות", route: 'login' },
      { name: "הרשמה", route: 'singup' }
    ];

  const [anchorElNav, setAnchorElNav] = useState(null);
  const [anchorElUser, setAnchorElUser] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const getAllRecipes = async () => {
      try {
        const response = await axios.get('http://localhost:3000/recipes');
        setRecipes(response.data);
      } catch (err) {
        console.error(err); 
      }
    };

    getAllRecipes(); 
}, []); 


  const handleOpenNavMenu = (event) => {
    setAnchorElNav(event.currentTarget);
  };

  const handleOpenUserMenu = (event) => {
    setAnchorElUser(event.currentTarget);
  };

  const handleCloseNavMenu = () => {
    setAnchorElNav(null);
  };

  const handleCloseUserMenu = () => {
    setAnchorElUser(null);
  };

  const handleNavigate = (page) => {
    const route = `/${page.route}`;
    navigate(route);
    handleCloseNavMenu();
  };

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const filteredPages = pages.filter((page) =>
    page.name.toLowerCase().includes(searchQuery.toLowerCase())
  );



  return (
    <AppBar position="static" sx={{ backgroundColor: '#dc4337', position: 'fixed', top: '0px', zIndex: 999 }}>
      <Container maxWidth="xl">
        <Toolbar disableGutters>

          <Link to="/home">
            <CottageIcon sx={{ display: { xs: 'none', md: 'flex' }, mr: 1, color: 'white' }} />
          </Link>
          <Typography
            variant="h6"
            noWrap
            component="a"
            href="#"
            sx={{
              mr: 2,
              display: { xs: 'none', md: 'flex' },
              fontFamily: 'monospace',
              fontWeight: 700,
              letterSpacing: '.3rem',
              color: 'inherit',
              textDecoration: 'none',
            }}
          >
            מתכונים
          </Typography>




          <Box sx={{ flexGrow: 1, display: { xs: 'flex', md: 'none' } }}>
            <IconButton
              size="large"
              aria-label="account of current user"
              aria-controls="menu-appbar"
              aria-haspopup="true"
              onClick={handleOpenNavMenu}
              color="inherit"
            >
              <MenuIcon />
            </IconButton>
            <Menu
              id="menu-appbar"
              anchorEl={anchorElNav}
              anchorOrigin={{
                vertical: 'bottom',
                horizontal: 'left',
              }}
              keepMounted
              transformOrigin={{
                vertical: 'top',
                horizontal: 'left',
              }}
              open={Boolean(anchorElNav)}
              onClose={handleCloseNavMenu}
            >
              {filteredPages.map((page, index) => (
                <MenuItem key={index} onClick={() => handleNavigate(page)}>
                  <Typography textAlign="center">{page.name}</Typography>
                </MenuItem>
              ))}
            </Menu>
          </Box>


          <CottageIcon sx={{ display: { xs: 'flex', md: 'none' }, mr: 1 }} />


          <Typography
            variant="h5"
            noWrap
            component="a"
            href="#"
            sx={{
              mr: 2,
              display: { xs: 'flex', md: 'none' },
              flexGrow: 1,
              fontFamily: 'monospace',
              fontWeight: 700,
              letterSpacing: '.3rem',
              color: 'inherit',
              textDecoration: 'none',
            }}
          >
            natali recipes
          </Typography>


          <Box sx={{ flexGrow: 1, display: { xs: 'none', md: 'flex' } }}>
            {filteredPages.map((page, index) => (
              <Button
                key={index}
                onClick={() => handleNavigate(page)}
                sx={{ my: 2, color: 'white', display: 'block' }}
              >
                {page.name}
              </Button>
            ))}
          </Box>

          {/* תיבת חיפוש */}
          <Box sx={{ flexGrow: 1, display: 'flex', justifyContent: 'center' }}>
            <Box sx={{ position: 'relative', display: 'flex', alignItems: 'center' }}>
              <SearchIcon sx={{ position: 'absolute', left: 10, color: 'white' }} />
              <InputBase
                sx={{
                  color: 'white',
                  border: '1px solid white',
                  borderRadius: '4px',
                  padding: '5px 10px',
                  paddingLeft: '30px',
                  width: '300px',
                }}
                placeholder="חפש מתכון..."
                value={searchQuery}
                onChange={handleSearchChange}
              />
            </Box>
          </Box>

          {user && (
            <Box sx={{ flexGrow: 0 }}>
              <Tooltip title="Open settings">
                <IconButton onClick={handleOpenUserMenu} sx={{ p: 0 }}>
                  <Avatar alt={user.username} src="/static/images/avatar/2.jpg" />
                </IconButton>
              </Tooltip>
              <Menu
                sx={{ mt: '45px' }}
                id="menu-appbar"
                anchorEl={anchorElUser}
                anchorOrigin={{
                  vertical: 'top',
                  horizontal: 'right',
                }}
                keepMounted
                transformOrigin={{
                  vertical: 'top',
                  horizontal: 'right',
                }}
                open={Boolean(anchorElUser)}
                onClose={handleCloseUserMenu}
              >
                {userpages.map((page, index) => (
                  <MenuItem key={index} onClick={() => handleNavigate(page)}>
                    <Typography textAlign="center">{page.name}</Typography>
                  </MenuItem>
                ))}
              </Menu>
            </Box>
          )}
        </Toolbar>
      </Container>
    </AppBar>
  );
};

export default NavBar;
