import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Container, Grid, Typography, Card, CardContent, CardMedia, Box, Paper } from '@mui/material';
import { styled } from '@mui/system';

const HomePage = () => {
  const [categories, setCategories] = useState([]);
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // בקשה לשרת כדי לקבל את כל הקטגוריות
    axios.get('http://localhost:3000/categories')
      .then(response => {
        setCategories(response.data);
      })
      .catch(err => console.error(err));

    // בקשה לשרת כדי לקבל את המתכונים הפופולריים (נניח שהנתיב הוא /recipes/popular)
    axios.get('http://localhost:3000/recipes/popular')
      .then(response => {
        setRecipes(response.data);
      })
      .catch(err => console.error(err))
      .finally(() => setLoading(false));
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <Container>
      {/* כותרת הדף */}
      <Typography variant="h3" gutterBottom align="center">
        Welcome to Our Recipe Collection!
      </Typography>

      {/* הצגת קטגוריות */}
      <Typography variant="h4" gutterBottom>
        Categories
      </Typography>
      <Grid container spacing={4} justifyContent="center">
        {categories.map(category => (
          <Grid item xs={12} sm={6} md={4} key={category._id}>
            <Card>
              <CardMedia
                component="img"
                height="140"
                image={`http://localhost:3000${category.image}`}
                alt={category.name}
              />
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  {category.name}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* ציטוטים מלקוחות */}
      <Box my={6}>
        <Typography variant="h4" gutterBottom align="center">
          What Our Customers Say
        </Typography>
        <Grid container spacing={4} justifyContent="center">
          {['"Amazing recipes, loved every dish!"', '"Great service and delicious meals!"', '"A perfect place for food lovers!"'].map((quote, index) => (
            <Grid item xs={12} sm={4} key={index}>
              <Paper elevation={3} sx={{ padding: 4, textAlign: 'center' }}>
                <Typography variant="h6" color="textSecondary">
                  {quote}
                </Typography>
              </Paper>
            </Grid>
          ))}
        </Grid>
      </Box>

      {/* תמונות של המתכונים הפופולריים */}
      <Typography variant="h4" gutterBottom align="center">
        Popular Recipes
      </Typography>
      <Grid container spacing={4} justifyContent="center">
        {recipes.map(recipe => (
          <Grid item xs={12} sm={6} md={4} key={recipe._id}>
            <Card>
              <CardMedia
                component="img"
                height="140"
                image={`http://localhost:3000${recipe.image}`}
                alt={recipe.name}
              />
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  {recipe.name}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default HomePage;
