import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const CategoryRecipe = () => {
  const { categoryId } = useParams();
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // בקשה לשרת כדי לקבל את המתכונים לפי ID של קטגוריה
    axios.get(`http://localhost:3000/recipes/category/${categoryId}`)
      .then(response => {
        setRecipes(response.data);
        setLoading(false);
      })
      .catch(err => {
        console.error(err);
        setLoading(false);
      });
  }, [categoryId]);

  if (loading) {
    return <div>טוען...</div>;
  }

  return (
    <div>
      <h2>מתכונים בקטגוריה</h2>
      <div>
        {recipes.map(recipe => (
          <div key={recipe._id}>
            <h3>{recipe.name}</h3>
            <img src={`http://localhost:3000${recipe.image}`} alt={recipe.name} />
            <p>{recipe.preparationTime} דקות הכנה</p>
            <p>{recipe.finalYield}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CategoryRecipe;
