// 
import * as React from 'react';
import axios from 'axios';
import {
  Button,
  FormControl,
  FormControlLabel,
  Checkbox,
  InputLabel,
  OutlinedInput,
  TextField,
  InputAdornment,
  Link,
  Alert,
  IconButton,
} from '@mui/material';
import AccountCircle from '@mui/icons-material/AccountCircle';
import Visibility from '@mui/icons-material/Visibility';
import VisibilityOff from '@mui/icons-material/VisibilityOff';
import { AppProvider } from '@toolpad/core/AppProvider';
import { SignInPage } from '@toolpad/core/SignInPage';
import { useTheme } from '@mui/material/styles';
import { useState } from 'react';

const providers = [{ id: 'credentials', name: 'Name and Password' }];

// Custom fields for the form
function CustomNameField() {
  return (
    <TextField
      id="input-with-icon-textfield"
      label="שם"
      name="name"
      type="text"
      size="small"
      required
      fullWidth
      slotProps={{
        input: {
          startAdornment: (
            <InputAdornment position="start">
              <AccountCircle fontSize="inherit" />
            </InputAdornment>
          ),
        },
      }}
      variant="outlined"
    />
  );
}

function CustomPasswordField() {
  const [showPassword, setShowPassword] = React.useState(false);

  const handleClickShowPassword = () => setShowPassword((show) => !show);

  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };

  return (
    <FormControl sx={{ my: 2 }} fullWidth variant="outlined">
      <InputLabel size="small" htmlFor="outlined-adornment-password">
        סיסמא
      </InputLabel>
      <OutlinedInput
        id="outlined-adornment-password"
        type={showPassword ? 'text' : 'password'}
        name="password"
        size="small"
        endAdornment={
          <InputAdornment position="end">
            <IconButton
              aria-label="toggle password visibility"
              onClick={handleClickShowPassword}
              onMouseDown={handleMouseDownPassword}
              edge="end"
              size="small"
            >
              {showPassword ? (
                <VisibilityOff fontSize="inherit" />
              ) : (
                <Visibility fontSize="inherit" />
              )}
            </IconButton>
          </InputAdornment>
        }
        label="סיסמא"
      />
    </FormControl>
  );
}

function CustomButton() {
  return (
    <Button
      type="submit"
      variant="outlined"
      color="info"
      size="small"
      disableElevation
      fullWidth
      sx={{ my: 2 }}
    >
      התחברות
    </Button>
  );
}

function SignUpLink() {
  return (
    <Link href="/" variant="body2">
      הרשמה
    </Link>
  );
}

function Title() {
  return <h2 style={{ marginBottom: 8 }}>התחברות</h2>;
}

function Subtitle({ errorMessage }) {
  return errorMessage ? (
    <Alert sx={{ mb: 2, px: 1, py: 0.25 }} severity="error">
      {errorMessage}
    </Alert>
  ) : null;
}

function AgreeWithTerms() {
  return (
    <FormControlLabel
      control={
        <Checkbox
          name="tandc"
          value="true"
          color="primary"
          sx={{ padding: 0.5, '& .MuiSvgIcon-root': { fontSize: 20 } }}
        />
      }
      slotProps={{
        typography: {
          fontSize: 14,
        },
      }}
      color="textSecondary"
      label="אני מסכימ/ה לתקנון"
    />
  );
}

export default function LogIn() {
  const theme = useTheme();
  const [userData, setUserData] = useState({
    name: '',
    password: '',
  });
  const [errorMessage, setErrorMessage] = useState(''); // State for error message

  React.useEffect(() => {
    console.log('user-data', userData);
  }, [userData]);

  const handleSignIn = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/api/login', {
        username: userData.name,
        password: userData.password,
      });

      // Handle successful login
      console.log('Logged in successfully:', response.data);
      setErrorMessage(''); // Reset error message if login is successful
      // Redirect user or save session token here
    } catch (error) {
      if (error.response && error.response.status === 404) {
        // Handle user not found error
        setErrorMessage('משתמש לא קיים');
      } else {
        // Handle general errors
        setErrorMessage('אירעה שגיאה, נסה שנית');
      }
    }
  };

  return (
    <AppProvider theme={theme}>
      <SignInPage
        signIn={(provider, formData) => {
          setUserData({
            name: formData.get('name'),
            password: formData.get('password'),
          });
          handleSignIn(formData); // Call the handleSignIn function when the form is submitted
        }}
        slots={{
          title: Title,
          subtitle: () => <Subtitle errorMessage={errorMessage} />,
          emailField: CustomNameField,
          passwordField: CustomPasswordField,
          submitButton: CustomButton,
          signUpLink: SignUpLink,
          rememberMe: AgreeWithTerms,
        }}
        providers={providers}
      />
    </AppProvider>
  );
}
