import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import { Container, Grid, Card, CardContent, CardMedia, Typography, Box, CircularProgress, IconButton } from '@mui/material';
import { UserContext } from './Context';
import DeleteIcon from '@mui/icons-material/Delete';
import { Link } from 'react-router-dom';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';

const CategoryRecipe = () => {
  const { categoryId, categoryName } = useLocation().state;
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const { user } = useContext(UserContext);

  // To track if user has liked a recipe
  const [userLikes, setUserLikes] = useState({});

  useEffect(() => {
    const getAllRecipes = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/recipes/category/${categoryId}`);
        setRecipes(response.data);
        setLoading(false);

        // Initialize the userLikes state with the current user likes status
        const likesStatus = {};
        response.data.forEach(recipe => {
          likesStatus[recipe._id] = recipe.likes.includes(user?._id); // Check if user already liked the recipe
        });
        setUserLikes(likesStatus);

      } catch (err) {
        console.error(err);
      }
    };
    getAllRecipes();
  }, [categoryId, user]); // Track changes to user data

  const deleteRecipe = async (recipeId) => {
    try {
      await axios.delete(`http://localhost:3000/recipes/${recipeId}`);
      setRecipes(prevRecipes => prevRecipes.filter(recipe => recipe._id !== recipeId));
    } catch (err) {
      console.error('Error deleting recipe:', err);
    }
  };

  const updateRecipeLikes = async (recipeId) => {
    if (userLikes[recipeId]) return; // If user already liked, don't allow another like

    try {
      // Add the current user to the likes array (assuming it stores an array of user IDs who liked the recipe)
      await axios.put(`http://localhost:3000/recipes/${recipeId}`, {
        $push: { likes: user._id } // Add user ID to the likes array in database
      });

      // Update the local state for userLikes and recipe's like count
      setUserLikes(prevLikes => ({
        ...prevLikes,
        [recipeId]: true
      }));

      setRecipes(prevRecipes => 
        prevRecipes.map(recipe => 
          recipe._id === recipeId ? { ...recipe, likes: recipe.likes + 1 } : recipe
        )
      );

    } catch (err) {
      console.error('Error updating likes:', err);
    }
  };

  if (loading) {
    return <CircularProgress />;
  }

  return (
    <Container sx={{ paddingTop: "80px" }}>
      <Typography gutterBottom align="center" variant="h4" style={{ textAlign: 'center', position: 'relative', paddingTop: '20px', paddingBottom: '20px' }}>
        <div style={{
          backgroundColor: 'black', height: '1px', width: '100%', position: 'absolute', top: '23px',
          zIndex: '-1', backgroundColor: 'black', height: '1px',
        }}></div>
        <p style={{
          paddingRight: '18px', paddingLeft: '18px', margin: 'auto', width: 'fit-content', backgroundColor: 'white',
          zIndex: 1
        }}>{categoryName}</p>
      </Typography>

      <Grid container spacing={4} justifyContent="center">
        {recipes.length === 0 ? (
          <Typography variant="h6" gutterBottom align="center">
            אין מתכונים בקטגוריה זו
          </Typography>
        ) : (
          recipes.map(recipe => (
            <Grid item xs={12} sm={6} md={4} key={recipe._id}>
              <Link to={`/recipe/${recipe._id}`} style={{ textDecoration: 'none' }}>
                <Card
                  sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    '&:hover': {
                      transform: 'scale(1.05)',
                      boxShadow: 10,
                      transition: 'transform 0.3s ease-in-out',
                    },
                    position: 'relative',
                  }}
                >
                  <CardMedia
                    component="img"
                    height="140"
                    image={`http://localhost:3000${recipe.image}`}
                    alt={recipe.name}
                  />
                  <CardContent sx={{ direction: 'rtl' }}>
                    <Typography variant="h6" gutterBottom sx={{ textAlign: 'center', textDecoration: 'underline' }}>
                      {recipe.name}
                    </Typography>
                    <Box display="flex" alignItems="center" mb={3}>
                      ⏳
                      <Typography variant="body2">{recipe.preparationTime} דקות</Typography>
                    </Box>
                    <Box display="flex" alignItems="center" mb={3}>
                      🍽
                      <Typography variant="body2">כמות: {recipe.finalYield}</Typography>
                    </Box>
                    <Box display="flex" alignItems="center" sx={{
                      padding: '5px 10px',
                      borderRadius: '50px',
                      alignItems: 'center',
                      marginBottom: '10px',
                      justifyContent: 'center',
                    }}>
                      {/* הצגת מספר הלייקים */}
                      <Typography variant="body2" sx={{ marginRight: '5px' }}>
                        {recipe.likes.length || 0} {/* מציג את מספר הלייקים */}
                      </Typography>
                      <IconButton 
                        onClick={() => updateRecipeLikes(recipe._id)}
                        disabled={userLikes[recipe._id]} // Disable if the user already liked
                      >
                        <ThumbUpIcon />
                      </IconButton>
                    </Box>
                  </CardContent>
                  {user && user.isAdmin && (
                    <Box
                      sx={{
                        position: 'absolute',
                        bottom: 10,
                        right: 10,
                      }}
                    >
                      <IconButton
                        sx={{ color: 'black' }}
                        onClick={(e) => {
                          e.stopPropagation();
                          deleteRecipe(recipe._id);
                        }}
                      >
                        <DeleteIcon />
                      </IconButton>
                    </Box>
                  )}
                </Card>
              </Link>
            </Grid>
          ))
        )}
      </Grid>
    </Container>
  );
};

export default CategoryRecipe;
