import React, {axios, useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { Container, Grid, Card, CardContent, CardMedia, Typography, Box } from '@mui/material';
import { Rating } from '@mui/material';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import CategoryIcon from '@mui/icons-material/Category';
import { Link } from 'react-router-dom'; // Import Link

const CategoryRecipe = () => {
  const { categoryId, categoryName } = useLocation().state;
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);

  console.log(categoryId,categoryName);


  // useEffect(async() => {

  //  await axios.get(`http://localhost:3000/recipes/category/${categoryId}`)
  //     .then(response => {
  //     ;

  //       setRecipes(response.data); 
  //       console.log(response.data);

  //       setLoading(false);
  //     })
  //     .catch(err => {
  //       console.error(err);
  //       setLoading(false);
  //     });
  // }, [categoryId]);

  useEffect(()=>{
    const getAllRecipes = async ()=>{
      try{
        const response = await axios.get();

      }
      catch(err){
        console.error(err);

      }
    }
    getAllRecipes();
  },[])

  if (loading) {
    return <div>טוען...</div>;
  }

  return (
    <Container>
      <Typography gutterBottom align="center" variant="h4" style={{ textAlign: "center", position: "relative", paddingTop: '20px', paddingBottom: '20px' }}>
        <div style={{
          backgroundColor: "black", height: "1px", width: "100%", position: 'absolute', top: '23px',
          zIndex: '-1', backgroundColor: 'black', height: '1px',
        }}></div>
        <p style={{
          paddingRight: "18px", paddingLeft: "18px", margin: "auto", width: "fit-content", backgroundColor: "white",
          zIndex: 1
        }}>{categoryName}</p>
      </Typography>

      <Grid container spacing={4} justifyContent="center">
        {recipes.length === 0 ? (
          <Typography variant="h6" gutterBottom align="center">
            לא נמצאו מתכונים בקטגוריה הזו
          </Typography>
        ) : (
          recipes.map(recipe => (
            <Grid item xs={12} sm={6} md={4} key={recipe._id}>

              <Link to={`/recipe/${recipe._id}`} style={{ textDecoration: 'none' }}>
                <Card
                  sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    '&:hover': {
                      transform: 'scale(1.05)',
                      boxShadow: 10,
                      transition: 'transform 0.3s ease-in-out',
                    },
                  }}
                >
                  <CardMedia
                    component="img"
                    height="140"
                    image={`http://localhost:3000${recipe.image}`}
                    alt={recipe.name}
                  />
                  <CardContent sx={{ direction: "rtl" }}>
                    <Typography variant="h6" gutterBottom sx={{ textAlign: 'center', textDecoration: 'underline' }}>
                      {recipe.name}
                    </Typography>
                    <Box display="flex" alignItems="center" mb={3}>
                      <AccessTimeIcon sx={{ mr: 1 }} />
                      <Typography variant="body2">{recipe.preparationTime} דקות</Typography>
                    </Box>
                    <Box display="flex" alignItems="center" mb={3}>
                      <CategoryIcon sx={{ mr: 1 }} />
                      <Typography variant="body2">כמות: {recipe.finalYield}</Typography>
                    </Box>
                    <Box display="flex" alignItems="center" sx={{
                      padding: '5px 10px', 
                      borderRadius: '50px',
                      alignItems: 'center',
                      marginBottom: '10px',
                      justifyContent: 'center',
                    }}>
                      <Rating 
                        name="read-only" 
                        value={recipe.likes / 10} 
                        readOnly 
                      />
                    </Box>
                  </CardContent>
                </Card>
              </Link>
            </Grid>
          ))
        )}
      </Grid>
    </Container>
  );
};

export default CategoryRecipe;
