import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { Container, Grid, Typography, Card, CardContent, CardMedia, Box, IconButton } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import { useNavigate } from 'react-router-dom';
import { UserContext } from './Context'; // אם יש לך קונטקסט לניהול המשתמש
// import AlertDialog from './AlertDialog'; // הוספת הדיאלוג

const Home = () => {
  const [categories, setCategories] = useState([]);
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [categoryToDelete, setCategoryToDelete] = useState(null); // קטגוריה למחיקה
  const [openDialog, setOpenDialog] = useState(false); // מצב של הדיאלוג

  const navigate = useNavigate();
  const { user } = useContext(UserContext); // משתמש מהקונטקסט, נניח שיש לך שדה 'isAdmin'

  useEffect(() => {
    axios.get('http://localhost:3000/categories')
      .then(response => {
        setCategories(response.data);
      })
      .catch(err => console.error(err));

    axios.get('http://localhost:3000/recipes/popular')
      .then(response => {
        setRecipes(response.data);
      })
      .catch(err => console.error(err))
      .finally(() => setLoading(false));
  }, []);

  if (loading) {
    return <div>טוען...</div>;
  }

  const handleDeleteCategory = (categoryId) => {
    setCategoryToDelete(categoryId);
    setOpenDialog(true);
  };

  const confirmDeleteCategory = () => {
    if (categoryToDelete) {
      axios.delete(`http://localhost:3000/categories/${categoryToDelete}`)
        .then(response => {
          setCategories(categories.filter(category => category._id !== categoryToDelete));
          console.log("Category deleted", response);
        })
        .catch(err => console.error("Error deleting category:", err));
    }
    setOpenDialog(false);
  };

  const cancelDeleteCategory = () => {
    setOpenDialog(false);
  };

  return (
    <Container>
      <Typography variant="h3" gutterBottom align="center">
        <Card style={{ position: 'relative', margin: '20px' }}>
          <CardMedia
            component="img"
            height="270"
            image={process.env.PUBLIC_URL + "888-10.jpg"}
            style={{ width: '100%' }}
          />
          <Box
            style={{
              position: 'absolute',
              top: '40%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
              color: 'white',
              fontSize: '50px',
              fontWeight: 'bold',
              padding: '10px',
              zIndex: 1
            }}
          >
            מתכונים שמחממים את הלב
          </Box>
        </Card>
      </Typography>

      <Typography variant="h4" gutterBottom style={{ textAlign: "center", position: "relative" }}>
        <div
          style={{
            backgroundColor: "black", height: "1px",
            width: "100%", position: 'absolute', top: '23px',
            zIndex: '-1',
            backgroundColor: 'black',
            height: '1px',
          }}></div>
        <p style={{
          paddingRight: "18px",
          paddingLeft: "18px",
          margin: "auto",
          width: "fit-content",
          backgroundColor: "white"
        }}>קטגוריות</p>
      </Typography>

      <Grid container spacing={4} justifyContent="center">
        {categories.map(category => (
          <Grid item xs={12} sm={6} md={6} key={category._id}>
            <Card
              onClick={() => navigate('/category-recipe', {
                state: {
                  categoryId: category._id,
                  categoryName: category.name
                }
              })}
              style={{
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                height: 'auto',
                position: 'relative',
                transition: 'transform 0.3s ease',
                cursor: 'pointer',
                boxShadow: 'none'
              }}
              onMouseEnter={(e) => e.currentTarget.style.transform = 'scale(1.05)'}
              onMouseLeave={(e) => e.currentTarget.style.transform = 'scale(1)'}
            >
              <CardMedia
                component="img"
                height="140"
                image={`http://localhost:3000${category.image}`}
                alt={category.name}
              />
              <CardContent style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                <Typography variant="h6" gutterBottom style={{ textAlign: 'center' }}>
                  {category.name}
                </Typography>
              </CardContent>

              {/* הצגת אייקון פח אם המשתמש הוא מנהל */}
              {user && user.isAdmin && (
                <IconButton 
                  onClick={(e) => {
                    e.stopPropagation();
                    handleDeleteCategory(category._id);
                  }}
                  sx={{
                    position: 'absolute',
                    bottom: '10px',
                    right: '10px',
                    color: 'black'
                  }}
                >
                  <DeleteIcon />
                </IconButton>
              )}
            </Card>
          </Grid>
        ))}
      </Grid>

      <Typography variant="h4" gutterBottom style={{ textAlign: "center", position: "relative" }}>
        <div
          style={{
            backgroundColor: "black", height: "1px",
            width: "100%", position: 'absolute', top: '23px',
            zIndex: '-1',
            backgroundColor: 'black',
            height: '1px',
          }}></div>
        <p style={{
          paddingRight: "18px",
          paddingLeft: "18px",
          margin: "auto",
          width: "fit-content",
          backgroundColor: "white"
        }}>מתכונים פופולרים</p>
      </Typography>

      <Grid container spacing={4} justifyContent="center">
        {recipes.map(recipe => (
          <Grid item xs={12} sm={6} md={6} key={recipe._id}>
            <Card>
              <CardMedia
                component="img"
                height="140"
                image={`http://localhost:3000${recipe.image}`}
                alt={recipe.name}
              />
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  {recipe.name}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* דיאלוג אישור מחיקה */}
      {/* <AlertDialog  */}
        open={openDialog}
        handleClose={cancelDeleteCategory}
        handleConfirm={confirmDeleteCategory}
      />
    </Container>
  );
};

export default Home;
