import React, { useState, useEffect } from "react";
import axios from "axios";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import FormHelperText from "@mui/material/FormHelperText";
import { useNavigate } from "react-router-dom";

const AddCategoryManager = () => {
  const [categoryData, setCategoryData] = useState({
    name: "",
  });
  const [image, setImage] = useState(null);
  const [errors, setErrors] = useState({
    name: "",
    image: "",
  });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setCategoryData({
      ...categoryData,
      [e.target.name]: e.target.value,
    });
  };

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  const validateForm = () => {
    const newErrors = { name: "", image: "" };
    if (categoryData.name.length < 3) {
      newErrors.name = "שם קטגוריה חייב להיות לפחות 3 תווים";
    }
    if (!image) {
      newErrors.image = "יש להוסיף תמונה";
    }
    setErrors(newErrors);

    return !newErrors.name && !newErrors.image;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    const formData = new FormData();
    formData.append("image", image);
    formData.append("name", categoryData.name);

    try {
      const response = await axios.post("http://localhost:3000/categories", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      if (response.status === 201) {
        alert("הקטגוריה נוספה בהצלחה!");
        setCategoryData({ name: "" });
        setImage(null);
        navigate("/home");
      } else {
        alert("הוספת הקטגוריה נכשלה.");
      }
    } catch (error) {
      console.error("Error adding category:", error);
      alert("אירעה שגיאה במהלך הוספת הקטגוריה.");
    }
  };

  // הוספת התמונה כרקע לכל הדף (ה- body)
  useEffect(() => {
    document.body.style.backgroundImage = 'url(/addCtegoryImg.jpg)';
    document.body.style.backgroundSize = 'cover';
    document.body.style.backgroundPosition = 'center';
    document.body.style.height = '100vh'; // לוודא שהתמונה מכסה את כל הגובה
    return () => {
      document.body.style.backgroundImage = ""; // לנקות את הרקע כאשר עוזבים את הדף
    };
  }, []);

  return (
    <Box
      sx={{
        position: "relative",
        top: "194px",
        right: "13px",
        p: 3,
        maxWidth: 600,
        mx: "auto",
        border: "1px solid #ccc",
        borderRadius: 2,
        mt: 5,
        backgroundColor: 'rgba(255, 255, 255, 0.8)',  // רקע לבן עם שקיפות
        padding: 4,
        borderRadius: 2,
        boxShadow: 3,
        width: '100%',
        maxWidth: 500,
      }}
    >
      <Typography variant="h4" gutterBottom sx={{}}>
        הוספת קטגוריה חדשה
      </Typography>
      <form onSubmit={handleSubmit}>
        <TextField
          label="שם קטגוריה"
          name="name"
          value={categoryData.name}
          onChange={handleChange}
          fullWidth
          margin="normal"
          error={Boolean(errors.name)}
          helperText={errors.name}
        />
        <TextField
          label="תמונה של הקטגוריה"
          type="file"
          inputProps={{ accept: "image/*" }}
          onChange={handleImageChange}
          fullWidth
          margin="normal"
          error={Boolean(errors.image)}
        />
        {errors.image && (
          <FormHelperText error>{errors.image}</FormHelperText>
        )}
        <Button type="submit" variant="contained" fullWidth sx={{ mt: 3 }}>
          הוסף קטגוריה
        </Button>
      </form>
    </Box>
  );
};

export default AddCategoryManager;
