const AddRecipeForm = () => {
  const [recipeData, setRecipeData] = useState({
    publishDate: "",
    categoryCode: "",
    preparationTime: "",
    ingredients: [],
    preparationSteps: [],
    finalYield: "",
    likes: "",
  });

  const [image, setImage] = useState(null);
  const [ingredientInput, setIngredientInput] = useState("");
  const [stepInput, setStepInput] = useState("");

  const handleChange = (e) => {
    setRecipeData({
      ...recipeData,
      [e.target.name]: e.target.value,
    });
  };

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  const addIngredient = () => {
    if (ingredientInput.trim()) {
      setRecipeData({
        ...recipeData,
        ingredients: [...recipeData.ingredients, ingredientInput.trim()],
      });
      setIngredientInput("");
    }
  };

  const addStep = () => {
    if (stepInput.trim()) {
      setRecipeData({
        ...recipeData,
        preparationSteps: [...recipeData.preparationSteps, stepInput.trim()],
      });
      setStepInput("");
    }
  };
  const handleSubmit = async (e) => {
    e.preventDefault();

    // יצירת FormData
    const formData = new FormData();
    formData.append("image", image); // ודא ששדה זה מוגדר כ"image"
    formData.append("publishDate", recipeData.publishDate);
    formData.append("categoryCode", recipeData.categoryCode);
    formData.append("preparationTime", recipeData.preparationTime);
    formData.append("ingredients", JSON.stringify(recipeData.ingredients));
    formData.append("preparationSteps", JSON.stringify(recipeData.preparationSteps));
    formData.append("finalYield", recipeData.finalYield);
    formData.append("likes", recipeData.likes);

    // הדפסת הנתונים לבדיקה
    for (let pair of formData.entries()) {
      console.log(pair[0], pair[1]); // זה צריך להדפיס את שם השדה והערך, כולל תמונה
    }

    try {
      formData.forEach(data=>console.log(data)
      )
      console.log('form-data',formData);

      const response = await axios.post("http://localhost:3000/recipes", formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      const result = await response.data;
      if (response.status === 200) {
        alert("Recipe added successfully!");
        setRecipeData({
          publishDate: "",
          categoryCode: "",
          preparationTime: "",
          ingredients: [],
          preparationSteps: [],
          finalYield: "",
          likes: "",
        });
        setImage(null);
      } else {
        alert("Failed to add recipe: " + result.error);
      }
    } catch (error) {
      console.error("Error adding recipe:", error);
      alert("An error occurred while adding the recipe.");
    }
  };

  return (
    <div>
      <h2>Add New Recipe</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <div>
          <label>Recipe Image:</label>
          <input type="file" accept="image/*" onChange={handleImageChange} required />
        </div>
        <div>
          <label>Publish Date:</label>
          <input type="date" name="publishDate" value={recipeData.publishDate} onChange={handleChange} required />
        </div>
        <div>
          <label>Category Code:</label>
          <input type="text" name="categoryCode" value={recipeData.categoryCode} onChange={handleChange} required />
        </div>
        <div>
          <label>Preparation Time (minutes):</label>
          <input type="number" name="preparationTime" value={recipeData.preparationTime} onChange={handleChange} required />
        </div>
        <div>
          <label>Ingredients:</label>
          <input type="text" value={ingredientInput} onChange={(e) => setIngredientInput(e.target.value)} />
          <button type="button" onClick={addIngredient}>Add Ingredient</button>
          <ul>
            {recipeData.ingredients.map((ing, index) => (
              <li key={index}>{ing}</li>
            ))}
          </ul>
        </div>
        <div>
          <label>Preparation Steps:</label>
          <input type="text" value={stepInput} onChange={(e) => setStepInput(e.target.value)} />
          <button type="button" onClick={addStep}>Add Step</button>
          <ul>
            {recipeData.preparationSteps.map((step, index) => (
              <li key={index}>{step}</li>
            ))}
          </ul>
        </div>
        <div>
          <label>Final Yield:</label>
          <input type="text" name="finalYield" value={recipeData.finalYield} onChange={handleChange} required />
        </div>
        <div>
          <label>Likes:</label>
          <input type="number" name="likes" value={recipeData.likes} onChange={handleChange} />
        </div>
        <button type="submit">Add Recipe</button>
      </form>
    </div>
  );
};

export default AddRecipeForm;
