import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Container, Grid, Card, CardContent, CardMedia, Typography, Box } from '@mui/material';
import FavoriteIcon from '@mui/icons-material/Favorite';

const CategoryRecipe = () => {
  const { categoryId } = useParams(); // קבלת ה- categoryId מתוך ה-URL
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // בקשה לשרת כדי לקבל את המתכונים לפי categoryId
    axios.get(`http://localhost:3000/recipes/category/${categoryId}`)
      .then(response => {
        console.log('response',response.data);

        setRecipes(response.data); // עדכון הנתונים של המתכונים
        setLoading(false); // סיום טעינה
      })
      .catch(err => {
        console.error(err); // טיפול בשגיאות
        setLoading(false); // סיום טעינה גם במקרה של שגיאה
      });
  }, [categoryId]); // התלוות ל- categoryId

  if (loading) {
    return <div>טוען...</div>;
  }

  return (
    <Container>
      <Typography variant="h3" gutterBottom align="center">
        מתכונים בקטגוריה
      </Typography>

      <Grid container spacing={4} justifyContent="center">
        {recipes.length === 0 ? (
          <Typography variant="h6" gutterBottom align="center">
            לא נמצאו מתכונים בקטגוריה הזו
          </Typography>
        ) : (
          recipes.map(recipe => (
            <Grid item xs={12} key={recipe._id}>
              <Card sx={{ display: 'flex', mb: 2 }}>
                {/* תמונה בצד שמאל */}
                <CardMedia
                  component="img"
                  sx={{ width: 200 }}
                  image={`http://localhost:3000${recipe.image}`}
                  alt={recipe.name}
                />

                {/* תוכן הכרטיס בצד ימין */}
                <Box sx={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', p: 2 }}>
                  <Typography variant="h5" gutterBottom>
                    {recipe.name}
                  </Typography>
                  <Typography variant="body1" color="textSecondary">
                    כמות: {recipe.finalYield}
                  </Typography>
                  <Typography variant="body1" color="textSecondary" display="flex" alignItems="center">
                    {recipe.likes} <FavoriteIcon sx={{ ml: 1, color: 'red' }} />
                  </Typography>
                </Box>
              </Card>
            </Grid>
          ))
        )}
      </Grid>
    </Container>
  );
};

export default CategoryRecipe;
